                    "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "autoRotate" : true,
                    "hoverPause" : true,
                    "transitionType" : "fade", // fade, slide, custom
                    "transitionSpeed" : 1.5,
                    "transitionDelay" : 4,
                    "fullScreenRotator" : true,
                    "fullScreenBreakpoints" : [], // NUMERICAL - [960, 768, 640, 480, 320]
                    "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                    "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                    "onTransitionStart" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                    "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                    "allLoaded" : function(props) {

                    		$("#hp-slideshow .mmg-container .mmg-control").insertBefore(".mmg-container .mmg-slides-outer");

                            $("#hp-slideshow .mmg-container .mmg-control.back").prepend('');
                            $("#hp-slideshow .mmg-container .mmg-control.back").prepend('');
                            $("#hp-slideshow .mmg-container .mmg-control.next").prepend('');
                            $("#hp-slideshow .mmg-container .mmg-control.next").prepend('');

                    		var windowWidth = $(window).width();
                            var contentWidth = $("#gb-global-icons").width();
                            var controlsOffset = (windowWidth - contentWidth)/2;
                            $("#hp-slideshow .mmg-container .mmg-slide .mmg-description").css("left",controlsOffset);
                            $("#hp-slideshow .mmg-container .mmg-control.back").css("left",controlsOffset);
                            $("#hp-slideshow .mmg-container .mmg-control.next").css("right",controlsOffset);

                            var slideshowHeight = $("#hp-slideshow-outer").height();
                            var headerHeight = $("#gb-header-outer").height();
                            var socialIconHeight = $("#gb-main-social-icons").height();
                            var displayHeight = (slideshowHeight - headerHeight)-50;
                            var socialIconOffset = ((displayHeight - socialIconHeight)/2) + headerHeight;
                            $("#gb-main-social-icons").css("top",socialIconOffset);


                            if($(".mmg-control.play-pause").length){
                            	$(".mmg-bullets-outer").append($(".mmg-control.play-pause"));
                            }
                            var bulletWidth = $("#hp-slideshow .mmg-bullets-outer").width();
                            var bulletOffset = bulletWidth/2;
                            $("#hp-slideshow .mmg-bullets-outer").css("margin-left",-bulletOffset);

                            $("#hp-slideshow .mmg-bullets-outer").insertBefore(".mmg-description-outer");

                            $(window).resize(function(){
                                var windowWidth = $(window).width();
                                var contentWidth = $("#gb-global-icons").width();
                                var controlsOffset = (windowWidth - contentWidth)/2;
                                $("#hp-slideshow .mmg-container .mmg-slide .mmg-description").css("left",controlsOffset);
                                $("#hp-slideshow .mmg-container .mmg-control.back").css("left",controlsOffset);
                            	$("#hp-slideshow .mmg-container .mmg-control.next").css("right",controlsOffset);

