                                var headerHeight = $("#gb-header-outer").height();
                                var socialIconHeight = $("#gb-main-social-icons").height();
                                var displayHeight = (slideshowHeight - headerHeight)-50;
                                var socialIconOffset = ((displayHeight - socialIconHeight)/2) + headerHeight;
                                $("#gb-main-social-icons").css("top",socialIconOffset);

