        "SLM":  function() {
            $.schoolListMinibase({
                'miniBaseDomainID': 8,
                'miniBaseModuleInstanceID': 38718,
                'cache': true,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */

                    var schools = '';

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            schools += '
' + schoolList[i].schoolName + '';
                        }
                    }

                    $('#gb-header-inner .cs-mystart-dropdown.schoollist .cs-dropdown-list').html(schools);
                    window.csTemplate.RsMenu(schoolList);
                }
            });

             // BIND DROPDOWN EVENTS
                this.DropdownActions({
                    "dropdownParent": ".cs-mystart-dropdown.schoollist",
                    "dropdownSelector": ".cs-selector",
                    "dropdown": ".cs-dropdown",
                    "dropdownList": ".cs-dropdown-list"
                });
        },
