         "ParentsDropdown":function() {

        	var parentsDropdown = [

                {
                    "show": true,
                    "text": "Canvas",
                    "url": "https://bvusd.instructure.com/login/canvas",
                    "target": "_blank"
                },
                {
                    "show": true,
                    "text": "ParentVUE",
                    "url": "https://bvpo.bluevalleyk12.org/PXP2_Login.aspx",
                    "target": "_blank"
                }
            ];

            var links = '';
			$.each(parentsDropdown, function(index, link) {
				if(link.show) {
					links += '
' + link.text + '';
				}
			});

            if(links.length) {
            	var _this = this;
				$(".parents").parent().addClass('parents-dd-trigger').append('' + links + '');



                //KEYBOARD ACESSIBILITY
                $('.parents-dd-trigger a').on('keydown', function(e) {
                	if(_this.AllyClick(e)) {
                	e.preventDefault();
                    	if($(this).hasClass('is-open')) {
                        	$('#parents-dd a').attr('tabindex', 0);
                        	$(this).removeClass('is-open').focus();
                            $('#parents-dd').slideUp('fast').attr('aria-expanded', 'false');


                        }else {
                        	$(this).addClass('is-open');
                            $('#parents-dd').slideDown('fast').attr('aria-expanded', 'true');
                            $('#parents-dd a').attr('tabindex', -1);
                            $('.parents-dd-trigger li:first').find('>a').focus();

                        }

                	}



                });


                $('#parents-dd a').on('click keydown', function(e) {
                	if(e.key == 'Tab') {
                    	$('.parents-dd-trigger a').removeClass('is-open').focus();
                        	$(this).removeClass('is-open');
                            $('#parents-dd a').attr('tabindex', -1);
                            $('#parents-dd').slideUp('fast').attr('aria-expanded', 'false');
                    }

                    //RIGHT AND DOWN
                    if(e.key == 'ArrowDown' || e.key == 'ArrowRight') {
                    	e.preventDefault();
                        if($(this).parent().is(":last-child")) {
                        	$('#parents-dd li:first').find('> a').focus();


                        }else {
                        	$(this).parent().next().find('a').focus();
                        }
                     }

                     //LEFT AND UP
                    if(e.key == 'ArrowLeft' || e.key == 'ArrowUp') {
                    	e.preventDefault();
                        if($(this).parent().is(":first-child")) {
                        	$('.parents').focus();


                        }else {
                        	$(this).parent().prev().find('a').focus();
                        }
                     }


                });

                $('.parents').on('keydown', function(e) {
                	if(e.key == 'ArrowUp') {
                    	e.preventDefault();
                        $('#parents-dd li:last').find('> a').focus();
                	}

                    if(e.key == 'Tab') {
                    	$(this).removeClass('is-open')
                        $('#parents-dd').slideUp('fast').attr('aria-expanded', 'false');
                        $('#parents-dd a').attr('tabindex', -1);

                    }

                });


                //HOVER EVENTS
                $('.parents-dd-trigger').hover(function() {
                	$(this).addClass('is-open');
                    $('#parents-dd').slideDown('fast').attr('aria-expanded', 'true');
                    $('#parents-dd a').attr('tabindex', 0);
                }, function() {
                	$(this).removeClass('is-open');
                    $('#parents-dd').slideUp('fast').attr('aria-expanded', 'false');
                    $('#parents-dd a').attr('tabindex', -1);
                })



			}
        },



