$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=EhPrWF0TjwPFDFUxZZjz7g%3d%3d&SessionID=1fd74c53-156d-434a-a3a1-f673ae80e456&SiteID=8&ChannelID=0&SectionID=0&PageID=9&HitDate=4%2f24%2f2020+8%3a02%3a10+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.27.65';
