            var channelsFound

            $("#gb-channel-list #sw-channel-list-container li.sw-channel-item a[aria-haspopup='true']").each(function(index){
            	var li = $(this).parent('li');
            	var i = ($(li).hasClass('navc-HP')) ? 0 : index;

                if( i < megaMenuData.length ){
                    if( megaMenuData[i].channelTitle == "" 
                      && megaMenuData[i].channelCaption == "" 
                      && megaMenuData[i].channelImgBool == 'false' 
                      && megaMenuData[i].channelCats[0].channelCatSections == ''
                      && megaMenuData[i].channelCats[1].channelCatSections == ''
                      && megaMenuData[i].channelCats[2].channelCatSections == ''
                      && megaMenuData[i].channelCats[3].channelCatSections == '' ){
                        $(li).addClass('no-mega');
                    }else{
                    	$(li).addClass('possible-mega-menu');
                    }
            	}else{
                	$(li).addClass('no-mega');
                }
            });

            // BUILD THE MEGA MENUS
            $("#gb-channel-list #sw-channel-list-container li.sw-channel-item.possible-mega-menu").each(function(i, k){
                //REPLACE ANY SECTION THAT CONTAINS & WITH AND
                $(".sw-channel-dropdown > li > a > span", this).text(function () {
                    return $(this).text().replace("&", "and"); 
                });

                // ADD MEGA CHANNEL CLASS
                $(this).addClass('mega-channel');
                $(this).addClass("Channel-" + i);


                // ADD CHANNEL CATEGORIES
                var categoryColumn = '
';
                var textNums = ["one", "two", "three", "four", "five", "six", "seven", "eight", "nine", "ten", "eleven", "twelve", "thirteen", "fourteen", "fifteen", "sixteen", "seventeen", "eighteen", "nineteen", "twenty"];
                var channelCats = megaMenuData[i].channelCats;
                var catCol = 0;
                var itemsAddedToList = 0;
                var maxItemsToAdd = 0;
                for( var d = 0; d < channelCats.length; d++ ){
                	maxItemsToAdd += (channelCats[d].channelCatSections != "") ? parseInt(channelCats[d].channelCatSections) : 0;
                }

                categoryColumn += '
';
                categoryColumn +=   '
' + channelCats[0].channelCatHeader + '';
                categoryColumn +=   '';

                $("> .sw-channel-dropdown li", this).each(function(liIndex, li){
                    if(channelCats[catCol].channelCatHeader != "" && channelCats[catCol].channelCatSections != ""){
                        categoryColumn += '
' + $(li).text() + '';
                        } else {
                            categoryColumn += '>' + $(li).text() + '';
                        }

                        itemsAddedToList++;
                        if(itemsAddedToList == channelCats[catCol].channelCatSections){
                            categoryColumn += 	'';
                            categoryColumn += '';
                            catCol++;
                            if(catCol == channelCats.length || liIndex == maxItemsToAdd - 1){
                                return false
                            }

                            categoryColumn += '
';
                            categoryColumn +=   '
' + channelCats[catCol].channelCatHeader + '';
                            categoryColumn +=   '';
                            itemsAddedToList = 0;
                        }
                    }else{
                        return false
                    }
                });

                categoryColumn +=	'';

                // ADD STRUCTURE TO THE DOM
                $(".sw-channel-dropdown", this).html('




');

                // ADD IMAGE
                var imgColumn = '';
                var imgSrc = jQuery.trim(megaMenuData[i].channelImg);
                var srcSplit = imgSrc.split("/");
                var srcSplitLen = srcSplit.length;
                var noImg = false;
                if(megaMenuData[i].channelImgBool != "false" && imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top").addClass('has-thumb');
                    imgColumn = '
';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-one", this).append(imgColumn);
                }else{
                    noImg = true;
                    $('.sw-channel-dropdown .mega-columns', this).addClass('no-img');
                }

                // ADD CHANNEL NAME
                var noTitle = false;
                if(megaMenuData[i].channelTitle != ""){
                    var channelColumn = '
' + megaMenuData[i].channelTitle + '';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).append(channelColumn);
                }else{
                    noTitle = true;
                }

                // ADD CAPTION
                var noCaption = false;
                if(jQuery.trim(megaMenuData[i].channelCaption) != "" ){
                    var captionColumn = '
' + jQuery.trim(megaMenuData[i].channelCaption) + '';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).append(captionColumn);
                }else{
                    noCaption = true;
                }

                if(noTitle && noCaption){
                    $('.sw-channel-dropdown .mega-columns', this).addClass('no-text');
                    if(noImg){
                        $('.sw-channel-dropdown .mega-menu-top', this).remove();
                        $('.sw-channel-dropdown .mega-columns', this).addClass('full-width');
                    }
                }

                $(".sw-channel-dropdown .mega-columns .mega-menu-bottom", this).append(categoryColumn);

                $('.sw-channel-dropdown .mega-channel-cat-outer li').addClass('mega-channel-cat-section-list-item');
            });    

            $("#gb-channel-list .mega-channel .mega-channel-cat .mega-channel-cat-section-list-item a").keydown(function(e) {

                    var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                    // CAPTURE KEY CODE
                    switch(e.keyCode) {
                        // CONSUME LEFT AND UP ARROWS
                        case csKeyCodes.left:
                        case csKeyCodes.up:
                            e.preventDefault();
                            e.stopImmediatePropagation();

                            // IS FIRST ITEM
                            if($(this).parent().is(":first-child") && !$(this).parent().parent().parent().is(":first-child")) {
                                // FOCUS LAST ITEM
                                $(this).parent().parent().parent().prev(".mega-channel-cat").find(".mega-channel-cat-section-list li:last-child > a").focus();
                            } else if($(this).parent().is(":first-child") && $(this).parent().parent().parent().is(":first-child")) {
                                $('.mega-channel.hover > a').focus();
                            } else {
                                // FOCUS PREVIOUS ITEM
                                $(this).parent().prev("li").find("> a").focus();
                            }
                        break;

                        // CONSUME RIGHT AND DOWN ARROWS
                        case csKeyCodes.right:
                        case csKeyCodes.down:
                            e.preventDefault();
                            e.stopImmediatePropagation();

                            // IS LAST ITEM
                            if($(this).parent().is(":last-child") && !$(this).parent().parent().parent().is(":last-child")) {
                                // FOCUS LAST ITEM
                                $(this).parent().parent().parent().next(".mega-channel-cat").find(".mega-channel-cat-section-list li:first-child > a").focus();
                            } else if($(this).parent().is(":last-child") && $(this).parent().parent().parent().is(":last-child")) {
                                $(this).parent().parent().parent().parent().find(".mega-channel-cat:first-child").find(".mega-channel-cat-section-list li:first-child > a").focus()    
                            } else {
                                // FOCUS NEXT ITEM
                                $(this).parent().next("li").find("> a").focus();
                            }
                        break;

                        // CONSUME HOME KEY
                        case csKeyCodes.home:
                            e.preventDefault();

                            // FOCUS FIRST ITEM
                            $(this).closest(".mega-channel-cat-section-list").find("li:first-child > a").focus();
                        break;

                        // CONSUME END KEY
                        case csKeyCodes.end:
                            e.preventDefault();

                            // FOCUS LAST ITEM
                            $(this).closest(".mega-channel-cat-section-list").find("li:last-child > a").focus();
                        break;
                    }
			});
        },


