    (function ($) {
        function checkLoader(query) {
            var existed = false;
            $('script').each(function () {
                var src = $(this).attr('src');
                if (src && src.indexOf(query) >= 0) {
                    existed = true;
                    return false;
                }
            })
            return existed;
        }

        if (!checkLoader('ko=true')) {
            document.write("");
        }
        if (!checkLoader('kom=true')) {
            document.write("");
        }
    }(jQuery));

