body.script:
$(document).ready(function(){
// Give empty div a usable class
$('.mura-index dl.first').parent('div').addClass('svIndex-wrapper');
// Fix Date to display correctly
// Hopefully this goes away and we do this in Mura instead
$('.svIndex-wrapper .releaseDate').each( function() {
currentDate = new Date( Date.parse( $(this).text() ));
// DateTimeFormatter formatter = DateTimeFormatter.ofPatter('MM.DD.YYYY');
formattedDate = currentDate.toString("MM.dd.yyyy");
// console.log(currentDate);
// console.log(formattedDate);
$(this).replaceWith('' + formattedDate + '');
});
// Fix Title to not have a link
// Hopefully this goes away and we do this in Mura instead
$('.svIndex-wrapper .title a').each( function() {
$(this).contents().unwrap();
});
// Truncate summary text for list view pages (folder/blog)
// Hopefully this goes away and we do this in Mura not jquery
$('.svIndex-wrapper .summary p').each( function() {
string = $(this).text();
if( string.length > 125 ) {
truncatedString = jQuery.trim(string).substring(0, 125).split(" ").slice(0, -1).join(" ") + "...";
$(this).replaceWith('
' + truncatedString + '');
};
});
// Global Navigation show/hide
$('#mcs-menu-btn').click(function() {
$('.global-nav-btn').removeClass('open');
$('.sub-navigation').addClass('hidden');
$('#main-navigation').toggleClass('hidden');
$('#translate-nav').hide();
if ($(this).hasClass('open')) {
$('#main, #video-masthead, .breadcrumbs, #search-results, #footer').removeClass('menu-overlaid');
$('body').removeClass('has-overlay');
} else {
$('#main, #video-masthead, .breadcrumbs, #search-results, #footer').addClass('menu-overlaid');
$('body').addClass('has-overlay');
};
$(this).toggleClass('open');
});
$('.global-nav-item').click(function() {
if (!( $(this).find('.global-nav-btn').hasClass('open') ) ){
$(this).siblings().find('.global-nav-btn').removeClass('open');
$('#main-navigation').addClass('hidden');
$('#mcs-menu-btn').removeClass('open');
$(this).siblings().find('.sub-navigation').addClass('hidden');
$(this).siblings().find('.global-nav-btn').removeClass('open');
$(this).find('.sub-navigation').toggleClass('hidden');
$('#translate-nav').hide();
// $(this).find('.global-nav-btn').toggleClass('open');
if ($(this).find('.global-nav-btn').hasClass('open')) {
$('#main, #video-masthead, .breadcrumbs, #search-results, #footer').removeClass('menu-overlaid');
$('body').removeClass('has-overlay');
} else {
$('#main, #video-masthead, .breadcrumbs, #search-results, #footer').addClass('menu-overlaid');
$('body').addClass('has-overlay');
};
$(this).find('.global-nav-btn').toggleClass('open');
};
});
$('#translate-menu').on('click', function() {
$('.sub-navigation, #main-navigation').addClass('hidden');
if ( $('#translate-nav').hasClass('translate-open') ) {
$('#main, #video-masthead, .breadcrumbs, #search-results, #footer').addClass('menu-overlaid');
$('.open').addClass('open');
$('body').addClass('has-overlay');
} else {
$('#main, #video-masthead, .breadcrumbs, #search-results, #footer').removeClass('menu-overlaid');
$('.open').removeClass('open');
$('body').removeClass('has-overlay');
};
})
$(document).click(function(e) {
if ( $('#mcs-menu-btn, .global-nav-btn').hasClass('open') ) {
if ( !($(e.target).hasClass('translate-btn')) ) {
if ( $(e.target).closest('#site-navigation').length === 0) {
$('.sub-navigation, #main-navigation').addClass('hidden');
$('#main, #video-masthead, .breadcrumbs, #search-results, #footer').removeClass('menu-overlaid');
$('.open').removeClass('open');
$('body').removeClass('has-overlay');
};
};
};
if ( $('#translate-nav').hasClass('translate-open') ) {
if ( $(e.target).closest('#site-translate').length === 0) {
$('#translate-nav').removeClass('translate-open').hide();
$('.sub-navigation, #main-navigation').addClass('hidden');
$('#main, #video-masthead, .breadcrumbs, #search-results, #footer').removeClass('menu-overlaid');
$('.open').removeClass('open');
$('body').removeClass('has-overlay');
};
};
});
// Hide mobile close btn on click
$('.mobile-close').on('click', 'button',function() {
if( $(this).parent().parent('#main-navigation').length ) {
openMenu = $(this).parent().parent().siblings('button.open');
openMenu.click();
console.log(openMenu);
};
if( $(this).parent().parent('.sub-navigation').length ) {
setTimeout(function(){
$('#main, #video-masthead, .breadcrumbs, #search-results, #footer').removeClass('menu-overlaid');
$('body').removeClass('has-overlay');
$('.sub-navigation').addClass('hidden');
$('.global-nav-btn').removeClass('open')
}, 50);
};
});
// Set vars based on window width
if($(window).width() >= 610){
positionShift = 390;
} else if($(window).width() < 610) {
positionShift = $(window).width();
};
// Main Navigation positioning
$('.has-children .hc-button').click(function() {
// Get left position and remove 390 from it to keep the individual menus properly aligned
leftPosition = $(this).closest('.items-ul').position().left;
console.log(positionShift);
newleftPosition = leftPosition - positionShift;
$(this).closest('.items-ul').css({ left: newleftPosition	});
// Find closest parent .items-ul, update it's left position, and move it out of view to the left
$(this).closest('.items-ul').removeClass('in-view').addClass('hide-left');
// Find closest child .items-ul and move it into view from right
$(this).siblings('.items-ul').removeClass('hide-right').addClass('in-view');
});
$('.back-link').click(function(){
// Get left position and add 390 to it to keep the individual menus properly aligned
leftPosition = $(this).parents('.items-ul').eq(1).position().left;
newLeftPosition = leftPosition + positionShift;
$(this).parents('.items-ul').eq(1).css({ left: newLeftPosition	});
$(this).parents('.items-ul').eq(1).removeClass('hide-left').addClass('in-view');
var $this = $(this);
setTimeout(function(){
$this.closest('.nested-navigation.in-view').removeClass('in-view').addClass('hide-right');
}, 202); // This delay should be greater than the transition time in the css file
});
$('#mcs-menu .active').each(function(){
// Add 'active-parent' to all 'active' parent ul elements
if ($('.has-children').hasClass('active')){
// Check to see if a parent class has 'active' and remove it
$(this).parents('.has-children').addClass('active-parent').removeClass('active');
} else {
$(this).parents('.has-children').addClass('active-parent');
}
})
// Make the correct menu show up when the page loads
$('#mcs-menu .active').parents('.items-ul').addClass('hide-left').removeClass('hide-right').css('left','0px');
$('#mcs-menu .active').parent('.items-ul').addClass('in-view').removeClass('hide-left').removeAttr('style');
if ( $('#main-nav-items').hasClass('hide-left') ) {
$('#main-nav-items').css('left', '-' + positionShift + 'px');
/* Menu Height Script */
// Find the height of an li (height set in css)
liHeight = parseInt( $('.main-nav-item').css('height').replace('px','') );
// Get the total number of list items for the menu in view
totalItems = $('.in-view').children('li').length;
totalItems += 1 //need to include back button
// Calculate the height of the menu by multiplying the number of items time their height
newHeight = totalItems * liHeight;
newHeight += 7; //Adding back the 7px removed for the top arrow
// Set main nav starting height
$('#main-navigation').css('height', newHeight);
} else {
/* Menu Height Script */
// Get the total number of list items for the main menu
mainNavItems = $('#main-nav-items').children('li').length;
// Find the height of an li (set in the css an may change)
mainNavHeight = parseInt($('.main-nav-item').css('height').replace('px',''));
// Calculate the height of the menu by multiplying the number of items time their height
mainInitialHeight = mainNavItems * mainNavHeight;
mainInitialHeight += 7; //Adding back the 7px removed for the top arrow
// Set main nav starting height
$('#main-navigation').css('height', mainInitialHeight);
}
// Reset main nav positioning on resize
