// Set height of nav when menu button is clicked
$('.hc-button').on('click',function(){
/* Get height of nav that is in view */
// Find the height of an li (height set in css)
liHeight = parseInt( $('.main-nav-item').css('height').replace('px','') );
// Get the total number of list items for the menu in view
totalItems = $('.in-view').children('li').length;
totalItems += 1 //need to include back button
// Calculate the height of the menu by multiplying the number of items time their height
newHeight = totalItems * liHeight;
newHeight += 7; //Adding back the 7px removed for the top arrow
// Set menu height
$('#main-navigation').css('height', newHeight);
});
// Set height of nav when menu button is clicked - back link
$('.back-link').on('click',function(){
// Get height of in-view's parent nav for larger views
if($(window).width() >= 610){
prevItems = $(this).parent('.items-ul').parent().parent('.items-ul');
prevItemsLength = prevItems.children('li').length;
if ( $(prevItems).hasClass('nested-navigation') ) {
prevItemsLength += 1 //need to include back button for all but main/home menu
};
prevMenuHeight = prevItemsLength * liHeight;
prevMenuHeight += 7;
// Set menu height
$('#main-navigation').css('height', prevMenuHeight);
};
});
