})
// Pause/Play for background masthead video
// Video
video = $('#masthead-video video');
// Button
pausePlayButton = $('#play-pause');
pausePlayButton.click(function() {
// console.log('click');
if( video.get(0).paused ) {
video.get(0).play();
// Change text to 'Pause'
pausePlayButton.text('Pause');
} else {
video.get(0).pause();
// Change text to 'Play'
pausePlayButton.text('Play');
};
// Toggle 'video-paused' class to change icon based on play/pause state.
pausePlayButton.toggleClass('video-paused');
});
