$('.i-am-page .link-group button, #directory-page .link-group button').click(function() {
var text = $(this).text();
$(this).text( text == "Expand" ? "Collapse" : "Expand" );
$(this).parent().parent().siblings().children('.lg-head').children('.opened').text("Expand");
$(this).parent().parent().siblings().children('.lg-links.opened').slideToggle('easy', function() {
$(this).removeClass('opened');
});
$(this).parent().parent().siblings().children('.lg-head').children('.opened').removeClass('opened');
$(this).toggleClass('opened');
$(this).closest('.link-group').children('.lg-links').slideToggle('easy', function() {
$(this).toggleClass('opened');
});
});
