/* Start Calendar Scripts */
var date = new Date();
var startCurMonth = new Date(date.getFullYear(), date.getMonth(), 1);
// var date = new Date();
// var d = date.getDate();
// var m = date.getMonth();
// var y = date.getFullYear();
// date.setDate();
$(document).on("click", "#cal-next-button" , function(){
startCurMonth.setMonth(startCurMonth.getMonth()+1);
// alert(startCurMonth);
// alert(startCurMonth.getMonth());
// alert(date.getFullYear());
$.get('/themes/default/remote/ajaxCal.cfm?siteid=default&contentid=00000000000000000000000000000000001&year='+startCurMonth.getFullYear()+'&month='+startCurMonth.getMonth(), function(data){
$('#calendar-wrapper').html(data);
loadCalendarEventHandlers();
});
});
$(document).on("click", "#cal-prev-button" , function(){
startCurMonth.setMonth(startCurMonth.getMonth()-1);
// alert(startCurMonth);
// alert(startCurMonth.getMonth());
// alert(date.getFullYear());
$.get('/themes/default/remote/ajaxCal.cfm?siteid=default&contentid=00000000000000000000000000000000001&year='+startCurMonth.getFullYear()+'&month='+startCurMonth.getMonth(), function(data){
$('#calendar-wrapper').html(data);
loadCalendarEventHandlers();
});
});
