$('.cal-event').each(function(){
$(this).siblings('.cal-week').children('.toggle-event').removeClass('hideme');
$(this).parent().siblings('.cal-month').children('.toggle-all').removeClass('hideme');
});
$('.cal-week').on('click', '.toggle-event', function(){
// if($(this).parent().parent().siblings('.cal-weeks').children('.cal-week').children('.toggle-event').hasClass('opened')) {
// $(this).parent().parent().siblings('.cal-weeks').children('.cal-week').children('.toggle-event').removeClass('opened');
// }
// $(this).parent().parent().siblings('.cal-weeks').children('.cal-event.opened').slideToggle('easy', function() {
// if ($(this).is(':visible')) {
// $(this).css('display','flex');
// };
// $(this).toggleClass('opened');
// });
thisMonth = $(this).parent().parent().parent();
$(this).toggleClass('opened');
$(this).parent('.cal-week').siblings('.cal-event').slideToggle('easy', function() {
if ($(this).is(':visible')) {
$(this).css('display','flex');
};
$(this).toggleClass('opened');
});
lengthCount = $(thisMonth).find('.toggle-event.opened').length;
if( lengthCount == 0 && $(thisMonth).find('.toggle-all').hasClass('all-visible')){
$(thisMonth).find('.toggle-all.all-visible').text('View All').removeClass('all-visible');
};
});
$('.cal-event').on('click', '.close-event', function(){
$(this).parent().siblings('.cal-week').children('.toggle-event').toggleClass('opened');
$(this).parent().slideToggle('easy', function() {
if ($(this).is(':visible')) {
$(this).css('display','flex');
};
$(this).toggleClass('opened');
});
});
// Click on 'current' day after page loads via setTimeout
setTimeout(function(){
$('.toggle-event.current').click();
}, 750)
// 'View All' cal button
$('.toggle-all').on('click', function(){
$(this).toggleClass('all-visible');
if ($(this).hasClass('all-visible')) {
$(this).text('Close All');
$(this).parent().parent().find('.toggle-event').not('.hideme').not('.opened').each(function(){
$(this).click();
});
$('.toggle-event').not('.hideme').addClass('opened');
} else {
$(this).text('View All');
$(this).parent().parent().find('.toggle-event.opened').each(function(){
$(this).click();
});
$('.toggle-event').not('.hideme').removeClass('opened');
};
});
/* End Calendar Scripts */
});
loadCalendarEventHandlers = function(){
// Need to do this in CF so there's no need for the timeout for the calendar to load
// setTimeout(function(){
// }, 500);
$('.cal-event').each(function(){
$(this).siblings('.cal-week').children('.toggle-event').removeClass('hideme');
});
$('.cal-week').on('click', '.toggle-event', function(){
if($(this).parent().parent().siblings('.cal-weeks').children('.cal-week').children('.toggle-event').hasClass('opened')) {
$(this).parent().parent().siblings('.cal-weeks').children('.cal-week').children('.toggle-event').removeClass('opened');
}
$(this).parent().parent().siblings('.cal-weeks').children('.cal-event.opened').slideToggle('easy', function() {
if ($(this).is(':visible')) {
$(this).css('display','flex');
$(this).toggleClass('opened');
};
});
$(this).toggleClass('opened');
$(this).parent('.cal-week').siblings('.cal-event').slideToggle('easy', function() {
if ($(this).is(':visible')) {
$(this).css('display','flex');
};
$(this).toggleClass('opened');
});
});
$('.cal-event').on('click', '.close-event', function(){
$(this).parent().siblings('.cal-week').children('.toggle-event').toggleClass('opened');
$(this).parent().slideToggle('easy', function() {
if ($(this).is(':visible')) {
$(this).css('display','flex');
};
$(this).toggleClass('opened');
});
});
