link.script:
'use strict';

// in case there are other version of jquery being imported with this
// version, we will use no conflict and IIFE to avoid global name space
var jq183 = jQuery.noConflict(true);

(function(jq183) {

	jq183(document).ready(function() {
		var alertKeyStartConstant = 'doNotShowAlert';

		var alertArticleKey =
			alertKeyStartConstant + '_' +
			'1198507_' +
			'2020/06/05 23:00:00';

		var alertExpirationDate = new Date('2020/06/05 23:00:00');

		// manually check for the alert expire time, if the alert expire
		// after now, we remove this item from local storage from user
		for (var key in localStorage) {
			// we only remove key when this key starts with the constant
			if (key.indexOf(alertKeyStartConstant) === 0) {
				var alertExpireAt = new Date(key.split("_")[2]);

				if (alertExpireAt.getTime() < new Date()) {
					window.localStorage.removeItem(key);
				}
			}
		}

		// show alert modal to user
		if (!window.localStorage.getItem(alertArticleKey)) {
			jq183.magnificPopup.open({
				items: {
					src: '#homepage_news_alert_modal',
					type: 'inline',
					showCloseBtn: false,
					closeBtnInside: false
				}
			});
		}

		// when user click on `don't show again`, we store this answer
		// in the localStorage
		jq183('.disable').click( function() {
			window.localStorage.setItem(alertArticleKey, true);
		});
	});

})(jq183);

body.script:
	window.onload = init;
	var topnavButton = document.getElementById('sitenav_main_mobile_toggle');
	var topnavDisplay = document.getElementById('sitenav_main');

	var stateDataButton = document.getElementById('state_toggle');
	var stateDataDisplay = document.getElementById('state_required');

	function init() {
		topnavButton.onclick = toggleNav;
		stateDataButton.onclick = toggleState;
	}

	function toggleNav(){
		topnavDisplay.classList.toggle("open");
	}
	function toggleState() {
		stateDataDisplay.classList.toggle("open");
	}

	window.addEventListener('scroll', function(event) {
    	var toggleElement = document.getElementById('header_logo');
    if ( window.pageYOffset != 0 ) {
        toggleElement.classList.add('zeigt');
        return;
    } else {
        toggleElement.classList.remove('zeigt');
        return;
    }
});
