        "JsMediaQueries": function () {
            switch (this.GetBreakPoint()) {
                case "desktop":
                    if ($("#gb-header-left #gb-logo").length) {
                        $("#gb-header-left #gb-logo").prependTo("#gb-header-middle");
                    }
                    if (!$(".hp-content-outer.three > .hp-content-outer.gb-icons-cont").length) {
                        //$("main > .hp-content-outer.gb-icons-cont").prependTo;
                        $(".hp-content-outer.gb-icons-cont").prependTo(".hp-content-outer.three");
                    }
                    if (!$("#gb-header #gb-header-right").length) {
                        //$("#gb-header-right").prependTo(".hp-content-outer.one > .hp-content");
                        $("#gb-header-middle").after($("#gb-header-right"));
                    }
                    break;
                case "768":
                    if ($("#gb-header-middle #gb-logo").length) {
                        $("#gb-header-left #gb-sitename").before($("#gb-header-middle #gb-logo"));
                    }
                    if ($(".hp-content-outer.three > .hp-content-outer.gb-icons-cont").length) {
                        //$("main > .hp-content-outer.gb-icons-cont").prependTo;
                        $(".hp-content-outer.one .hp-transparency-container").after($(".hp-content-outer.three > .hp-content-outer.gb-icons-cont"));
                    }
                    if ($("#gb-header #gb-header-right").length) {
                        //$("#gb-header-right").prependTo(".hp-content-outer.one > .hp-content");
                        $(".hp-content-outer.one .hp-transparency-container").after($("#gb-header-right"));
                    }
                    if (!$(".gb-footer.two .social-media-header").length) {
                        $(".social-media-header").appendTo(".gb-footer.two");
                    }
                    if (!$(".gb-footer.two .social-media-icons").length) {
                        $(".social-media-icons").appendTo(".gb-footer.two");
                    }
                    break;
                case "640":
                    if ($("#gb-header-middle #gb-logo").length) {
                        $("#gb-header-left #gb-sitename").before($("#gb-header-middle #gb-logo"));
                    }
                    if ($(".hp-content-outer.three > .hp-content-outer.gb-icons-cont").length) {
                        //$("main > .hp-content-outer.gb-icons-cont").prependTo;
                        $(".hp-content-outer.one .hp-transparency-container").after($(".hp-content-outer.three > .hp-content-outer.gb-icons-cont"));
                    }
                    if ($("#gb-header #gb-header-right").length) {
                        //$("#gb-header-right").prependTo(".hp-content-outer.one > .hp-content");
                        $(".hp-content-outer.one .hp-transparency-container").after($("#gb-header-right"));
                    }
                    if (!$(".gb-footer.two .social-media-header").length) {
                        $(".social-media-header").appendTo(".gb-footer.two");
                    }
                    if (!$(".gb-footer.two .social-media-icons").length) {
                        $(".social-media-icons").appendTo(".gb-footer.two");
                    }
                    break;
                case "480":
                    if ($("#gb-header-middle #gb-logo").length) {
                        $("#gb-header-left #gb-sitename").before($("#gb-header-middle #gb-logo"));
                    }
                    if ($(".hp-content-outer.three > .hp-content-outer.gb-icons-cont").length) {
                        //$("main > .hp-content-outer.gb-icons-cont").prependTo;
                        $(".hp-content-outer.one .hp-transparency-container").after($(".hp-content-outer.three > .hp-content-outer.gb-icons-cont"));
                    }
                    if (!$(".gb-footer.two .social-media-header").length) {
                        $(".social-media-header").appendTo(".gb-footer.two");
                    }
                    if (!$(".gb-footer.two .social-media-icons").length) {
                        $(".social-media-icons").appendTo(".gb-footer.two");
                    }
                    break;
                case "320":
                    if ($("#gb-header-middle #gb-logo").length) {
                        $("#gb-header-left #gb-sitename").before($("#gb-header-middle #gb-logo"));
                    }
                    if (!$(".gb-footer.two .social-media-header").length) {
