                    }
                    if (!$(".gb-footer.two .social-media-icons").length) {
                        $(".social-media-icons").appendTo(".gb-footer.two");
                    }
                    break;
            }
        },

        "SLM": function () {
            var _this = this;

            $.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 7131,
                "minibaseFields": ["schoolName", "schoolType", "schoolUrl", "schoolAlias", "schoolVisible", "specialDesignation"],
                'cache': true,
                'schoolListReady': function (schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */

                    var schools = '';

                    for (var i in schoolList) {
                        if (schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            schools += '
' + schoolList[i].schoolName + '';
                        }
                    }

                    $('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);


                    var elemSchools = '', middleSchools = '', highSchools = '', earlyChildhoodSchools = '', exceptionalStudentSchools = '', adultEdSchools = '', careerSchools = '';

                    for (var i in schoolList) {
                        if (schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            if (schoolList[i].schoolType.toLowerCase().includes('elementary') && schoolList[i].specialDesignation.toLowerCase() == '') {
                                elemSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('elementary') && schoolList[i].specialDesignation.toLowerCase() == 'cs') {
                                elemSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('elementary') && schoolList[i].specialDesignation.toLowerCase() == 'es') {
                                elemSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('elementary') && schoolList[i].specialDesignation.toLowerCase() == 'is') {
                                elemSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('elementary') && schoolList[i].specialDesignation.toLowerCase() == 'ms' || schoolList[i].specialDesignation.toLowerCase() == 'm') {
                                elemSchools += '
' + schoolList[i].schoolName + '';
                            }

                            if (schoolList[i].schoolType.toLowerCase().includes('middle') && schoolList[i].specialDesignation.toLowerCase() == '') {
                                middleSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('middle') && schoolList[i].specialDesignation.toLowerCase() == 'cs') {
                                middleSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('middle') && schoolList[i].specialDesignation.toLowerCase() == 'es') {
                                middleSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('middle') && schoolList[i].specialDesignation.toLowerCase() == 'is') {
                                middleSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('middle') && schoolList[i].specialDesignation.toLowerCase() == 'ms' || schoolList[i].specialDesignation.toLowerCase() == 'm') {
                                middleSchools += '
' + schoolList[i].schoolName + '';
                            }

                            if (schoolList[i].schoolType.toLowerCase().includes('high') && schoolList[i].specialDesignation.toLowerCase() == '') {
                                highSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('high') && schoolList[i].specialDesignation.toLowerCase() == 'cs') {
                                highSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('high') && schoolList[i].specialDesignation.toLowerCase() == 'es') {
                                highSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('high') && schoolList[i].specialDesignation.toLowerCase() == 'is') {
                                highSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('high') && schoolList[i].specialDesignation.toLowerCase() == 'ms' || schoolList[i].specialDesignation.toLowerCase() == 'm') {
                                highSchools += '
' + schoolList[i].schoolName + '';
                            }

                            if (schoolList[i].schoolType.toLowerCase().includes('early') || schoolList[i].schoolType.toLowerCase().includes('childhood') && schoolList[i].specialDesignation.toLowerCase() == '') {
                                earlyChildhoodSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('early childhood') && schoolList[i].specialDesignation.toLowerCase() == 'cs') {
                                earlyChildhoodSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('early childhood') && schoolList[i].specialDesignation.toLowerCase() == 'es') {
                                earlyChildhoodSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('early childhood') && schoolList[i].specialDesignation.toLowerCase() == 'is') {
                                earlyChildhoodSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('early childhood') && schoolList[i].specialDesignation.toLowerCase() == 'ms' || schoolList[i].specialDesignation.toLowerCase() == 'm') {
                                earlyChildhoodSchools += '
' + schoolList[i].schoolName + '';
                            }

                            if (schoolList[i].schoolType.toLowerCase().includes('exceptional') || schoolList[i].schoolType.toLowerCase().includes('student') || schoolList[i].schoolType.toLowerCase().includes('education') && schoolList[i].specialDesignation.toLowerCase() == '') {
                                exceptionalStudentSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('exceptional student education') && schoolList[i].specialDesignation.toLowerCase() == 'cs') {
                                exceptionalStudentSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('exceptional student education') && schoolList[i].specialDesignation.toLowerCase() == 'es') {
                                exceptionalStudentSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('exceptional student education') && schoolList[i].specialDesignation.toLowerCase() == 'is') {
                                exceptionalStudentSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('exceptional student education') && schoolList[i].specialDesignation.toLowerCase() == 'ms' || schoolList[i].specialDesignation.toLowerCase() == 'm') {
                                exceptionalStudentSchools += '
' + schoolList[i].schoolName + '';
                            }

                            if (schoolList[i].schoolType.toLowerCase().includes('adult') || schoolList[i].schoolType.toLowerCase().includes('education') && schoolList[i].specialDesignation.toLowerCase() == '') {
                                adultEdSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('adult education') && schoolList[i].specialDesignation.toLowerCase() == 'cs') {
                                adultEdSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('adult education') && schoolList[i].specialDesignation.toLowerCase() == 'es') {
                                adultEdSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('adult education') && schoolList[i].specialDesignation.toLowerCase() == 'is') {
                                adultEdSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('adult education') && schoolList[i].specialDesignation.toLowerCase() == 'ms' || schoolList[i].specialDesignation.toLowerCase() == 'm') {
                                adultEdSchools += '
' + schoolList[i].schoolName + '';
                            }

                            if (schoolList[i].schoolType.toLowerCase().includes('career') || schoolList[i].schoolType.toLowerCase().includes('technical') && schoolList[i].specialDesignation.toLowerCase() == '') {
                                careerSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('career and technical') && schoolList[i].specialDesignation.toLowerCase() == 'cs') {
                                careerSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('career and technical') && schoolList[i].specialDesignation.toLowerCase() == 'es') {
                                careerSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('career and technical') && schoolList[i].specialDesignation.toLowerCase() == 'is') {
                                careerSchools += '
' + schoolList[i].schoolName + '';
                            } else if (schoolList[i].schoolType.toLowerCase().includes('career and technical') && schoolList[i].specialDesignation.toLowerCase() == 'ms' || schoolList[i].specialDesignation.toLowerCase() == 'm') {
                                careerSchools += '
' + schoolList[i].schoolName + '';
                            }
                        }
                    }

                    $('.gb-school-list-column.elem ul').append(elemSchools);
                    $('.gb-school-list-column.middle ul').append(middleSchools);
                    $('.gb-school-list-column.high ul').append(highSchools);
                    $('.gb-school-list-column.early-childhood ul').append(earlyChildhoodSchools);
                    $('.gb-school-list-column.student-ed ul').append(exceptionalStudentSchools);
                    $('.gb-school-list-column.adult-ed ul').append(adultEdSchools);
                    $('.gb-school-list-column.career-tech ul').append(careerSchools);
                    $(".gb-school-list-header").attr("aria-hidden", "true");
                    $(".gb-school-list-header.active").attr("aria-hidden", "false");

                    // TAB HEADER CLICK
                    $("#gb-school-list .gb-school-list-header").click(function (e) {
                        e.preventDefault();
                        // IF THE SELECTED TAB ISNT ALREADY ACTIVE
                        if (!$(this).hasClass("active")) {
                            // GET CLASS OF SELECTED TAB
                            var selectedList = $(this).attr("class");
                            selectedList = jQuery.trim(selectedList.replace("gb-school-list-header ", ""));

                            //REMOVE CURRENT ACTIVE CLASSES
                            $("#gb-school-list .gb-school-list-header.active, #gb-school-list .gb-school-list-section.active").removeClass("active");
                            $(".gb-school-list-header").attr("aria-hidden", "true");

                            // SET NEW ACTIVE CLASSES
                            $(this).addClass("active");
                            $("#gb-school-list .gb-school-list-section." + selectedList).addClass("active");
                            $(".gb-school-list-header.active").attr("aria-hidden", "false");
                        }


                    });
                    $(document).on("click keydown", "#gb-schools-toggle", function (e) {
                        if (_this.AllyClick(e)) {
                            e.preventDefault();

                            if ($("#gb-links-toggle").attr("aria-expanded") == "true") {
                                $("#gb-links-toggle").click();
                            }

                            if ($(this).attr("aria-expanded") == "false") {
                                $(this).attr("aria-expanded", "true");

                                $("#gb-schools-outer").slideDown(function () {
                                    $("#gb-school-list-headers #tab1", this).focus();
                                }).attr("aria-hidden", "false");
                            } else {
                                $(this).attr("aria-expanded", "false");

                                $("#gb-schools-outer").slideUp().attr("aria-hidden", "true");
                            }
                        }
                    });
                    $(document).on("keyup", function () {
                        if ($("#gb-schools-outer").attr("aria-hidden") == "false" && !$("#gb-schools").find(":focus").length && !$("#gb-schools-toggle").is(":focus")) {
                            $("#gb-schools-toggle").attr("aria-expanded", "false");
                            $("#gb-schools-outer").slideUp().attr("aria-hidden", "true");
                        }
                    });

                    // CLOSE BUTTON
                    $(document).on("click keydown", ".gb-close-schools", function (e) {
                        if (_this.AllyClick(e)) {
                            e.preventDefault();

                            $("#gb-schools-toggle").attr("aria-expanded", "false");
                            $("#gb-schools-outer").slideUp().attr("aria-hidden", "true");
                        }
                    });

                    // SCHOOL TAB KEYDOWN
                    $(".gb-school-list-header").keydown(function (e) {

                        var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                        var lastIndex = $(".gb-school-list-header").length - 1;

                        switch (event.keyCode) {
                            // CONSUME LEFT AND UP ARROWS
                            case csKeyCodes.left:
                            case csKeyCodes.up:
                                event.preventDefault();

                                // FOCUS THE PREVIOUS TAB
                                if ($(this).index() == 0) {
                                    $(".gb-school-list-header:last-child").focus();
                                } else {
                                    $(".gb-school-list-header").eq($(this).index() - 1).focus();
                                }
                                break;

                            // CONSUME RIGHT AND DOWN ARROWS
                            case csKeyCodes.right:
                            case csKeyCodes.down:
                                event.preventDefault();

                                // FOCUS THE NEXT TAB
                                if ($(this).index() == lastIndex) {
                                    $(".gb-school-list-header:first-child").focus();
                                } else {
                                    $(".gb-school-list-header").eq($(this).index() + 1).focus();
                                }
                                break;

                            // CONSUME HOME KEY
                            case csKeyCodes.home:
                                event.preventDefault();

                                // FOCUS THE FIRST TAB
                                $(".gb-school-list-header:first-child").focus();
                                break;

                            // CONSUME END KEY
                            case csKeyCodes.end:
                                event.preventDefault();

                                // FOCUS THE LAST TAB
                                $(".gb-school-list-header:last-child").focus();
                                break;
                        }

                    });

                    // SCHOOL TAB FOCUS
                    $(".gb-school-list-header").focus(function () {
                        // ADJUST TAB ATTRIBUTES
                        $(".gb-school-list-header").attr({ "tabindex": "-1", "aria-selected": "false" }).removeClass("active");
                        $(this).attr({ "tabindex": "0", "aria-selected": "true" }).addClass("active");
                        $(".gb-school-list-header").attr("aria-hidden", "true");

                        // ADJUST PANEL ATTRIBUTES
                        $(".gb-school-list-section").attr({ "aria-hidden": "true" }).removeClass("active");
                        $("#" + $(this).attr("aria-controls")).attr({ "aria-hidden": "false" }).removeClass("active");
                        $("#" + $(this).attr("aria-controls") + " li a").attr({ "tabindex": "0" });
                        $(".gb-school-list-header.active").attr("aria-hidden", "false");
                    });

                    // SCHOOL TAB KEYDOWN
                    $(".gb-school-list-section li a").keydown(function (e) {

                        var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                        switch (event.keyCode) {
                            // CONSUME LEFT AND UP ARROWS
                            case csKeyCodes.left:
                            case csKeyCodes.up:
                                event.preventDefault();

                                // IS FIRST ITEM
                                if ($(this).parent().is(":first-child")) {
                                    // FOCUS LAST ITEM
                                    $(this).closest("ul").find("li:last-child > a").focus();
                                } else {
                                    // FOCUS PREVIOUS ITEM
                                    $(this).parent().prev("li").find("> a").focus();
                                }

                                break;

                            // CONSUME RIGHT AND DOWN ARROWS
                            case csKeyCodes.right:
                            case csKeyCodes.down:
                                event.preventDefault();

                                // IS LAST ITEM
                                if ($(this).parent().is(":last-child")) {
                                    // FOCUS FIRST ITEM
                                    $(this).closest("ul").find("li:first-child > a").focus();
                                } else {
                                    // FOCUS NEXT ITEM
                                    $(this).parent().next("li").find("> a").focus();
                                }

                                break;

                            // CONSUME HOME KEY
                            case csKeyCodes.home:
                                event.preventDefault();

                                // FOCUS FIRST ITEM
                                $(this).closest("ul").find("li:first-child > a").focus();
                                break;

                            // CONSUME END KEY
                            case csKeyCodes.end:
                                event.preventDefault();

                                // FOCUS LAST ITEM
                                $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                                break;

                        }

                    });

                    //window.CreativeTemplate.RsMenu(schoolList);
                }
            });
        },

        "TabbedBlocks": function () {
            $(".cs-tabbed-region #sw-content-container2.ui-hp, .cs-tabbed-region #sw-content-container3.ui-hp, .cs-tabbed-region #sw-content-container4.ui-hp, .sp .cs-tabbed-region, .spn .cs-tabbed-region").tabBlocks();
        },

        "CookieMonster": {
            "createCookie": function (name, value, days) {
                var expires;

                if (days) {
                    var date = new Date();
                    date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
                    expires = "; expires=" + date.toGMTString();
                } else {
                    expires = "";
                }
                document.cookie = escape(name) + "=" + escape(value) + expires + "; path=schoolwires.net";
            },

            "readCookie": function (name) {
                var nameEQ = escape(name) + "=";
                var ca = document.cookie.split(';');
                for (var i = 0; i < ca.length; i++) {
                    var c = ca[i];
                    while (c.charAt(0) === ' ') c = c.substring(1, c.length);
                    if (c.indexOf(nameEQ) === 0) return unescape(c.substring(nameEQ.length, c.length));
                }
                return null;
            },

            "eraseCookie": function (name) {
                CreativeTemplate.CookieMonster.createCookie(name, "", false);
            }
        },

        "MyStart": function () {
            var _this = this;

            // ADD TRANSLATE
            $(".cs-mystart-dropdown.translate .cs-dropdown").creativeTranslate({
                "type": 2,
                "advancedOptions": {
                    "brandedLayout": 2 // 1 = VERTICAL (TEXT UNDER SELECT), 2 = HORIZONTAL (TEXT BESIDE SELECT), 3 = NON-RESPONSIVE MEGA DROPDOWN
                }
            });

            // ADD EXTRA LANGUAGES TO TRANSLATE DROPDOWN
            $("#gb-languages ul").clone().removeClass("displayFlexRow").addClass("cs-extra-languages displayFlexCol").appendTo(".cs-mystart-dropdown.translate .cs-dropdown");
            $(".cs-extra-languages > a").attr({
                "aria-hidden": "true",
                "tabindex": "-1"
            });

            $("#gb-languages > ul > li > a, .cs-extra-languages > li > a").click(function (e) {
                e.preventDefault();

                // GET THE NEW LANGUAGE CODE
                //CreativeTemplate.CookieMonster.eraseCookie("googtrans");
                CreativeTemplate.CookieMonster.createCookie("googtrans", $(this).attr('data-lang-code'));
                location.reload();
            }).keydown(function (e) {
                // ENTER KEY
                if (e.keyCode == 13) {
                    CreativeTemplate.CookieMonster.createCookie("googtrans", $(this).attr('data-lang-code'));
                    location.reload();
                }
            });


            // BIND DROPDOWN EVENTS
            this.DropdownActions({
                "dropdownParent": ".cs-mystart-dropdown",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });

            // SEARCH
            $("#sw-search-button").attr({
                "role": "button",
                "aria-label": "Search Our Site"
            });

            //MOVE SEARCH OUT OF SYSTEM MYSTART
            $("#sw-mystart-search").prependTo("#gb-search");
            //SEARCH DIALOG
            $(document).on("click keydown", "#gb-search-toggle", function (e) {
                if (_this.AllyClick(e)) {
                    e.preventDefault();

                    if ($(this).attr("aria-expanded") == "false") {
                        $(this).attr("aria-expanded", "true");

                        $("#gb-search").slideDown(function () {
                            $("#sw-search-input").focus();
                        }).attr("aria-hidden", "false");
                    } else {
                        $(this).attr("aria-expanded", "false");

                        $("#gb-search").slideUp().attr("aria-hidden", "true");
                    }
                }
            });
            $(document).on("keyup", function () {
                if ($("#gb-search").attr("aria-hidden") == "false" && !$("#gb-search").find(":focus").length && !$("#gb-search-toggle").is(":focus")) {
                    $("#gb-search-toggle").attr("aria-expanded", "false");
                    $("#sw-search-outer").slideUp().attr("aria-hidden", "true");
                }
            });
            $("#sw-search-input").keydown(function (e) {

                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                switch (event.keyCode) {
                    case (e.shiftKey && csKeyCodes.tab):
                        e.preventDefault();
                        $("#gb-search-toggle").focus().click();
                        break;

                }

            });
            $("#sw-search-button").keydown(function (e) {

                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                switch (event.keyCode) {
                    case (e.shiftKey && csKeyCodes.tab):
                        e.preventDefault();
                        $("#sw-search-input").focus().click();
                        break;

                    case csKeyCodes.tab:
                        e.preventDefault();
                        $("#gb-search-toggle").focus().click();
                        break;

                }

            });

            //BANK OF LINKS
            $(document).on("click keydown", "#gb-links-toggle", function (e) {
                if (_this.AllyClick(e)) {
                    e.preventDefault();

                    if ($("#gb-schools-toggle").attr("aria-expanded") == "true") {
                        $("#gb-schools-toggle").click();
                    }

                    if ($(this).attr("aria-expanded") == "false") {
                        $(this).attr("aria-expanded", "true");

                        $("#gb-header-links").slideDown(function () {
                            $("ul:first-child > li:first-child > a", this).focus();
                        }).attr("aria-hidden", "false");
                    } else {
                        $(this).attr("aria-expanded", "false");

                        $("#gb-header-links").slideUp().attr("aria-hidden", "true");
                    }
                }
            });
            /*$(document).on("keyup", function() {
                if($("#gb-header-links").attr("aria-hidden") == "false" && !$("#gb-header-links a").find(":focus").length && !$("#gb-links-toggle").is(":focus")) {
                    $("#gb-links-toggle").attr("aria-expanded", "false");
                    $("#gb-header-links").slideUp().attr("aria-hidden", "true");
                }
            });*/
            $("#gb-header-links ul.links-left li a").keydown(function (e) {

                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                switch (event.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        event.preventDefault();

                        // IS FIRST ITEM
                        if ($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $("ul.special-link-cont li > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }

                        break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        event.preventDefault();

                        // IS LAST ITEM
                        if ($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $("ul.links-right li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }

                        break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        event.preventDefault();

                        // FOCUS FIRST ITEM
                        $("ul.links-left li:first-child > a").focus();
                        break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        event.preventDefault();

                        // FOCUS LAST ITEM
                        $("ul.special-link-cont li > a").focus();
                        break;

                    case csKeyCodes.tab:
                        $("#gb-links-toggle").click();
                        break;

                }

            });

            $("#gb-header-links ul.links-right li a").keydown(function (e) {

                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                switch (event.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        event.preventDefault();

                        // IS FIRST ITEM
                        if ($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $("ul.links-left li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }

                        break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        event.preventDefault();

                        // IS LAST ITEM
                        if ($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $("ul.special-link-cont li > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }

                        break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        event.preventDefault();

                        // FOCUS FIRST ITEM
                        $("ul.links-left li:first-child > a").focus();
                        break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        event.preventDefault();

                        // FOCUS LAST ITEM
                        $("ul.special-link-cont li > a").focus();
                        break;

                    case csKeyCodes.tab:
                        $("#gb-links-toggle").click();
                        break;


                }

            });

            $("#gb-header-links ul.special-link-cont li a").keydown(function (e) {

                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                switch (event.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        event.preventDefault();

                        $("#gb-header-links-inner ul.links-right").find("li:last-child > a").focus();

                        break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        event.preventDefault();

                        $("#gb-header-links-inner ul.links-left").find("li:first-child > a").focus();

                        break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        event.preventDefault();

                        // FOCUS FIRST ITEM
                        $("ul.links-left li:first-child > a").focus();
                        break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        event.preventDefault();

                        // FOCUS LAST ITEM
                        $("ul.special-link-cont li > a").focus();
                        break;

                    case csKeyCodes.tab:
                        $("#gb-links-toggle").click();
                        break;


                }

            });

            $("#gb-header-links-inner ul li > a > span").each(function () {
                var thisLink = this;
                if ($(thisLink).text() == "") {
                    $(thisLink).parent().parent().remove();
                }
            });

            if (!$("#gb-header-links-inner .links-right li").length) {
                $("#gb-header-links-inner .links-right").remove();
                $("#gb-header-links-inner .links-left").addClass("full-width");
                $("#gb-header-links").addClass("smaller-width");
            }
            if (!$("#gb-header-links-inner .links-left li").length) {
                $("#gb-header-links-inner .links-left").remove();
                $("#gb-header-links-inner .links-right").addClass("full-width");
                ("#gb-header-links").addClass("smaller-width");
            }
        },

        "DropdownActions": function (params) {
            /* ASSUMES THE FOLLOWING STRUCTURE



User Options




Link



            */

            // FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function (e) {
                e.preventDefault();

                if ($(this).parent().hasClass("open")) {
                    $("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                } else {
                    $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing");

                }
            });

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function (e) {
                // CAPTURE KEY CODE
                switch (e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if ($(dropdownParent).hasClass("open")) {
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        } else {
                            $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function () {
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                        break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if ($("+ " + dropdown + " " + dropdownList + " a").length) {
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                        break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                        break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function (e) {
                // CAPTURE KEY CODE
                switch (e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if ($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                        break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if ($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                        break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if (e.shiftKey) {
                            e.preventDefault();

                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                        break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                        break;
                }
            });

            $(dropdownParent).mouseleave(function () {
                $(dropdownList + " a", this).attr("tabindex", "-1");
                $(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
            }).focusout(function () {
                var thisDropdown = this;

                setTimeout(function () {
                    if (!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        },

        "Header": function () {
            // ADD LOGO
            var showLogo = true;
            var footerLogoToggle = false;
            var logoSrc = $.trim("/cms/lib/MI50000060/Centricity/Template/GlobalAssets/images///logos/default-logo.png");
            var footerLogoSrc = $.trim("/cms/lib/MI50000060/Centricity/Template/GlobalAssets/images///logos/default-logo.png");
            if (showLogo) {
                if ((logoSrc != "") && logoSrc.indexOf("default-man") == -1) {
                    $("#gb-logo").append('');
                } else {
                    $("#gb-logo").append('');
                }
            }
            if (footerLogoToggle) {
                if ((footerLogoSrc != "") && footerLogoSrc.indexOf("default-man") == -1) {
                    $("#footer-logo").append('');
                } else {
                    $("#footer-logo").append('');
                }
            } else {
                if ((logoSrc != "") && logoSrc.indexOf("default-man") == -1) {
                    $("#footer-logo").append('');
                } else {
                    $("#footer-logo").append('');
                }
            }

        },

        "StickyHeader": function () {
            if (!this.IsMyViewPage) {
                switch (this.GetBreakPoint()) {
                    case "desktop":
                        var scrollPosition = $(window).scrollTop();

                        var headerOffSet = $("header").offset().top + $("#gb-header-outer").outerHeight(true);
                        if (scrollPosition <= headerOffSet) {
                            $("header").css("height", "auto");
                            if (this.HeaderIsSticky) {
                                this.HeaderIsSticky = false;

                                //$("#gb-sticky-header").addClass("hidden");
                                $("#gb-header-outer").removeClass("sticky").removeAttr("style");
                                //$("nav").removeAttr("style");
                            }

                            if ($(".spn").length && $(".ui-widget.app.calendar").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
                                if ($(".wcm-controls").hasClass("wcm-stuck")) {
                                    $(".wcm-controls").css("margin-top", $("#gb-header-outer").outerHeight(true));
                                }
                            }
                        } else {
                            $("header").css("height", $("#gb-header-outer").outerHeight(true));

                            if (!this.HeaderIsSticky) {
                                this.HeaderIsSticky = true;

                                //$("#gb-sticky-header").removeClass("hidden");
                                $("#gb-header-outer").css("top", "-" + $("#gb-header-outer").outerHeight(true) + "px").addClass("sticky");
                                $("#gb-header-outer").animate({
                                    "top": "0px"
                                }, 0);
                            }

                            if ($(".spn").length && $(".ui-widget.app.calendar").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
                                if ($(".wcm-controls").hasClass("wcm-stuck")) {
                                    $(".wcm-controls").css("margin-top", $("#gb-header-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-header-outer").outerHeight(true) + "px"
                        });
                        break;
                    case "768": case "640": case "480": case "320":
                        if (this.HeaderIsSticky) {
                            this.HeaderIsSticky = false;

                            //$("#gb-sticky-header").addClass("hidden");
                            $("#gb-header-outer").removeClass("sticky").removeAttr("style");
                            //$("nav").removeAttr("style");
                        }

                        if ($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
                            if ($(".wcm-controls").hasClass("wcm-stuck")) {
                                $(".wcm-controls").css("margin-top", $("#gb-header-outer").outerHeight(true));
                            }
                        }
                        $("#sw-maincontent").removeAttr("style");
                        break;
                }
            }
        },

        "StickyGlobalIcons": function (getWindowHeight) {
            if ($(".hp").length) {
                if (!this.IsMyViewPage) {
                    switch (this.GetBreakPoint()) {
                        case "desktop":
                            var gbIconsOffSet = (!this.GlobalIconsAreSticky) ? $(".hp-content-outer.three").offset().top : $(".hp-content-outer.three").offset().top + $(".hp-content-outer.three").outerHeight(true);
                            var region3Top = $(".hp-content-outer.three").offset().top;
                            var region2Bottom = $(".hp-content-outer.two").offset().top + $(".hp-content-outer.two").outerHeight(true);
                            if ($(".hp-content-outer.gb-icons-cont.not-sticky").length) {
                                var gbIconsBottom = $(".hp-content-outer.gb-icons-cont.not-sticky").offset().top - $(".hp-content-outer.gb-icons-cont.not-sticky").outerHeight(true);
                            }
                            var scrollBottom = $(window).scrollTop() + CreativeTemplate.getWindowHeight - $("#gb-header-outer.sticky").outerHeight(true);
                            var scrolled = $(window).scrollTop();
                            if ($("#gb-header-outer.sticky").length) {
                                scrolled = scrolled - $("#gb-header-outer.sticky").outerHeight(true);
                            }

                            if (scrollBottom >= region2Bottom) {
                                if (this.GlobalIconsAreSticky) {
                                    $(".hp-content-outer.gb-icons-cont").removeClass("sticky").addClass("not-sticky");
                                    this.GlobalIconsAreSticky = false;
                                }
                            } else {
                                if (!this.GlobalIconsAreSticky) {
                                    $(".hp-content-outer.gb-icons-cont").removeClass("not-sticky").addClass("sticky");
                                    this.GlobalIconsAreSticky = true;
                                }
                            }
                            break;
                        case "768": case "640": case "480": case "320":
                            if (this.GlobalIconsAreSticky) {
                                $("header").removeClass("sticky").removeAttr("style");

                                this.GlobalIconsAreSticky = false;
                            }
                            break;
                    }
                }
            }
        },

        "ChannelBar": function () {
            $(".sw-channel-item").unbind("hover");
            $(".sw-channel-item").hover(function () {
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function () {
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });
        },

