CreativeTemplate.MyStart();
$(function(){
        window.console.log('dpscd:init function called');
        $(document).on("dpscd:getData", function() {
            window.console.log('dpscd:getData has been triggered');
            school = dpscd.data.filter(function(s) {
                    return s.shortName.toLowerCase() == siteAlias.toLowerCase();
            })[0];
            $(document).trigger("dpscd:getSchoolData", school);
            schools = dpscd.data.sort(dpscd.sortBySchoolName);

            // fix lunch menu global icon
            if (typeof school !== 'undefined' && typeof school.lunchMenus.Lunch !== 'undefined') {
                $("a.cs-global-icon:contains(Lunch Menu)").attr('href',school.lunchMenus.Lunch);
            }
            //add feedback url
            if (typeof school !== 'undefined'){
                dpscd.addFeedbackUrl(school);
            }
        });

        $(document).on("dpscd:setData", function() {
            console.log("dpscd:setData");
            $(document).trigger("dpscd:getData", schools);
        });

        dpscd.getAPIData();
});

