div.script:
$(document).ready(function () {
    $("#a4ab47ec-6925-4174-a90e-4734acb6b782").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#cf4aa535-1b4d-4f59-ace1-70abc6305d15").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#cf4aa535-1b4d-4f59-ace1-70abc6305d15').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#cf4aa535-1b4d-4f59-ace1-70abc6305d15').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 6 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#cde213fa-4c23-4959-8813-d48129058392').hide();
    jQuery('#cde213fa-4c23-4959-8813-d48129058392').listSolrResults({
        currentPagePath: '/content/schools/mvca/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: 'cde213fa-4c23-4959-8813-d48129058392',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

