function showSubList(id){
	resetSubList();
	document.getElementById(id).style.display="";
}

function resetSubList(){
	document.getElementById("subELA").style.display="none";
	document.getElementById("subMath").style.display="none";
	document.getElementById("subScience").style.display="none";
	document.getElementById("subSocStudies").style.display="none";
	document.getElementById("subTech").style.display="none";
	document.getElementById("subAMPS").style.display="none";
	document.getElementById("Blank").style.display="none";
	document.getElementById("subPREK").style.display="none";

}

function showDNList(id){
	resetDNList(id);
	document.getElementById(id).style.display="";
}

function resetDNList(list){
	if (list != "II"){
		resetSubList();
	}
	document.getElementById("II").style.display="none";
	document.getElementById("LN").style.display="none";
	document.getElementById("ISS").style.display="none";
	document.getElementById("TM").style.display="none";
	document.getElementById("ILR").style.display="none";
	document.getElementById("PLD").style.display="none";
	document.getElementById("TCR").style.display="none";
	document.getElementById("CD").style.display="none";
	document.getElementById("PMD").style.display="none";
	document.getElementById("PSD").style.display="none";

}

function showDDList(id){
	resetDNList(id);
	document.getElementById(id).style.display="";
}

function resetDDList(list){
	document.getElementById("CD").style.display="none";
	document.getElementById("PM").style.display="none";
	document.getElementById("PSD").style.display="none";
}



function showMenus(sender){
	var pId = sender.parentElement.parentElement.id;
	var pClass = sender.parentElement.parentElement.className;
	var tid = sender.textContent;
	var id = tid.replace(/\s/g,"");
	resetDisplay();
	showGrade(pId)
	showCoach(id);
	setGradeActive(pId);
	
	if(pClass!="gradeClick"){
		hideGradeSub("gradeClick")
	}
	
	var showSub = pId.replace("m","")+id;
	document.getElementById("trouble").innerHTML = pId + " " +id +" show Subject:"+showSub;
	showSubject(showSub);
	
}
var priorShowSub;
function showSubject(id){
	document.getElementById(id).style.display="inline";
	if(priorShowSub != null && priorShowSub != id){
		resetSubjectDisplay(priorShowSub);
	}
	priorShowSub = id;
}

function resetSubjectDisplay(pid){
	document.getElementById(pid).style.display="none";
}

function hideGradeSub(cN){
	if(cN == "gradeClick"){
		document.getElementsByClassName(cN)[0].style.display="none";
	}

}

function setGradeActive(pId){
	resetGradeActive();
	document.getElementById(pId).classList.add('curr-grade-active');
}

function resetGradeActive(){
	if(typeof document.getElementsByClassName("curr-grade-active")[0] != "undefined"){
		document.getElementsByClassName("curr-grade-active")[0].classList.remove('curr-grade-active');
	}
}
function showMobileMenu(){
		if(document.getElementById("showMobile").style.display=="none"){
		document.getElementById("showMobile").style.display="";
		
	}else{
		document.getElementById("showMobile").style.display="none";
	}
	
}
function showGradeSub(sender){
	hideGradeSub("gradeClick");
	document.getElementById("trouble").innerHTML = sender.id;
	document.getElementsByClassName("gradeClick")[0].style.display="inline-block";
	document.getElementsByClassName("gradeClick")[0].id=sender.id;
	document.getElementById("gradeTitle").innerHTML = sender.childNodes[0].nodeValue;
	
}
function resetDisplay(){
	document.getElementById("Mathmatics").style.display="none";
	document.getElementById("ELA").style.display="none";
	document.getElementById("Science").style.display="none";
	document.getElementById("SocialStudies").style.display="none";
	document.getElementById("Technology").style.display="none";
	document.getElementsByClassName("pre-k")[0].style.display="none";
	document.getElementsByClassName("k")[0].style.display="none";
	document.getElementsByClassName("g1")[0].style.display="none";
	document.getElementsByClassName("g2")[0].style.display="none";
	document.getElementsByClassName("g3")[0].style.display="none";
	document.getElementsByClassName("g4")[0].style.display="none";
	document.getElementsByClassName("g5")[0].style.display="none";
	document.getElementsByClassName("g6")[0].style.display="none";
	document.getElementsByClassName("g7")[0].style.display="none";
	document.getElementsByClassName("g8")[0].style.display="none";
	document.getElementsByClassName("g9")[0].style.display="none";
}
function showGrade(gId){
	document.getElementsByClassName(gId)[0].style.display="";
}
function showCoach(cId){
	document.getElementById(cId).style.display="";
}

(function (w, d, u) {
    if(w.njQuery === u){
        w.bindNextendQ = [];
    
        function pushToReady(x) {
            w.bindNextendQ.push([alias.handler,"ready", x]);
        }
        
        function pushToLoad(x) {
            w.bindNextendQ.push([alias.handler,"load", x]);
        }

        var alias = {
            handler: w,
            ready: pushToReady,
            load: pushToLoad
        }

        w.njQuery = function (handler) {
            alias.handler = handler;
            return alias;
        }
    }
})(window, document);

