jQuery(document).ready(function() {
  //change the integers below to match the height of your upper dive, which I called
  //banner.  Just add a 1 to the last number.  console.log($(window).scrollTop())
  //to figure out what the scroll position is when exactly you want to fix the nav
  //bar or div or whatever.  I stuck in the console.log for you.  Just remove when
  //you know the position.
  jQuery(window).scroll(function () { 

    if (jQuery(window).scrollTop() > 99) {
      jQuery('.navigation-row').addClass('navbar-fixed-top');
	  jQuery('.transparencybutton').addClass('transparency-fixed');
	  jQuery('.navbar-fixed-top').width(jQuery('#header').width());
    }

    if (jQuery(window).scrollTop() < 100) {
      jQuery('.navigation-row').removeClass('navbar-fixed-top');
	  jQuery('.transparencybutton').removeClass('transparency-fixed');
    }
  });
});
jQuery(window).resize(function() {
	jQuery('.navbar-fixed-top').width(jQuery('#header').width());
});
jQuery('.clickexpandheader').click(function () {
	jQuery(this).closest('div').find('.clickexpand').toggle();
});
jQuery('.clearbutton').click(function () {
	jQuery('.transparencybutton').toggle();
});

