//This scipt for Bootstrap 3 sub-menu dropdown on mobile view//
(function($){
    $(document).ready(function(){
        $('ul.dropdown-menu [data-toggle=dropdown]').on('click', function(event) {
            event.preventDefault();
            event.stopPropagation();
            $(this).parent().siblings().removeClass('open');
            $(this).parent().toggleClass('open');
        });
    });
})(jQuery);

div.script:
            $(window).load(function(){
            	var iframe = $('iframe.goog-te-menu-frame').contents();
                iframe.keydown(function(e){
                	if (e.keyCode == 40) { // down arrow get first link
                		e.preventDefault();
	                    e.stopPropagation();
	                    $(this).find('a:first').focus();
                	}
                });
                var menu = iframe.find('a').keydown(function(e){
                	var index = menu.index($(this));
                	if (e.keyCode == 40) { // down arrow
						e.preventDefault();
	                    e.stopPropagation();
						menu.eq(index + 1).focus();
					}
					if (e.keyCode == 38 && index > 0) { // up arrow
						e.preventDefault();
	                    e.stopPropagation();
						menu.eq(index - 1).focus();
					}
                });

            });

