}(document, 'script', 'facebook-jssdk'));

img.script:
// Bug 76422

		window.addEventListener('load', myInit, true);

		function myInit(){

		$('#datepicker14 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker14 a.ui-datepicker-prev').click();
	    		handlePrevClick14();
	    	}
	    });
	    $('#datepicker14 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker14 a.ui-datepicker-next').click();
	    		handleNextClick14();
	    	}
	    });


		function handlePrevClick14(){
			setTimeout(function () {
				triggerClick14();
			    $('#datepicker14').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick14(){
			setTimeout(function () {
				triggerClick14();
			    $('#datepicker14').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick14(){
			prev = $('#datepicker14').find('.ui-datepicker-prev');
			next = $('#datepicker14').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick14);
			$(prev).on('click', handlePrevClick14);
		}

		};



$(document).ready(function() {
var server = 'no';
if (server=='yes') {
	var firstLoad14 = true;
}else{
	var firstLoad14 = ($.fn.jquery.split('.')[1])<6?true:false;
}
var doe=[];
var load_doe14=function(y,m,callback){
  if(y.length!=4||m.length!=2) return;
  $.getJSON(window.location.protocol+'//'+window.location.host+'/calendar/doe/'+y+'/'+m, callback);
}
var proc_doe14=function(y,m,callback){
  var s=y+'-'+m;
  if(doe.indexOf(s)==-1){
    doe.push(s);
    load_doe14(y,m, function(data){
      if(data.code==200){
        for(key in data.response){
          var s=data.response[key].game_date;
          doe.indexOf(s)==-1&&doe.push(s);
        }
      }
      callback();
    });
  }else callback();
}

var cal_init14=function(){
  proc_doe14('2020','04',function(){
    $('#cal_loadimg14').hide();
    jQuery('#datepicker14').datepicker({
      prevText: ' ',
      nextText: ' ',
      onSelect: function(dateText, inst) {
//       	inst.inline = false;
        if (!firstLoad14) {
          var calendar_url = 'http://www.hvlconference.org/public/genie/10/school/1/?categories=30';
          if(calendar_url.length<3)return;
          var selected_date = new Date(dateText);
          var additional_url = 'date/' +selected_date.getFullYear()+'-'+ (parseInt(selected_date.getMonth()) + 1) +'-' + selected_date.getDate() + '/view/day/';
calendar_url += additional_url;
          window.open(calendar_url,'_blank');
        }
        firstLoad14 = false;
      },
      onChangeMonthYear: function(y,m,obj){
        y+='';m=(m<10?'0':'')+m;
        doe.indexOf(y+'-'+m)==-1&&proc_doe14(y,m,function(){
          console.log(doe);
          jQuery('#datepicker14').datepicker();
        });
        setTimeout(function () {

		$('#datepicker14 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker14 a.ui-datepicker-prev').click();
	    		handlePrevClick14();
	    	}
	    });
	    $('#datepicker14 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker14 a.ui-datepicker-next').click();
	    		handleNextClick14();
	    	}
	    });


		function handlePrevClick14(){
			setTimeout(function () {
				triggerClick14();
			    $('#datepicker14').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick14(){
			setTimeout(function () {
				triggerClick14();
			    $('#datepicker14').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick14(){
			prev = $('#datepicker14').find('.ui-datepicker-prev');
			next = $('#datepicker14').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick14);
			$(prev).on('click', handlePrevClick14);
		}

        }, 50);
      },
      beforeShowDay : function(date) {
        var y=date.getFullYear()+'',m=date.getMonth()+1,d=date.getDate();
        m=(m<10?'0':'')+m;d=(d<10?'0':'')+d;
        return [ true, doe.indexOf(y+'-'+m+'-'+d)>0?'call-bold':'', '' ];
      },
    });
  });
}
if(!jQuery.fn.datepicker){
  var scr=parseInt($.fn.jquery.split('.')[1])<6?'jquery-ui.min.js':'jquery-ui-1.9.2.min.js';
  $.getScript('/misc/'+scr, function(){cal_init14();});
}else{cal_init14();}
