div.script:
            $(window).load(function(){
            	var iframe = $('iframe.goog-te-menu-frame').contents();
                iframe.keydown(function(e){
                	if (e.keyCode == 40) { // down arrow get first link
                		e.preventDefault();
	                    e.stopPropagation();
	                    $(this).find('a:first').focus();
                	}
                });
                var menu = iframe.find('a').keydown(function(e){
                	var index = menu.index($(this));
                	if (e.keyCode == 40) { // down arrow
						e.preventDefault();
	                    e.stopPropagation();
						menu.eq(index + 1).focus();
					}
					if (e.keyCode == 38 && index > 0) { // up arrow
						e.preventDefault();
	                    e.stopPropagation();
						menu.eq(index - 1).focus();
					}
                });

            });

img.script:
// Bug 76422

		window.addEventListener('load', myInit, true);

		function myInit(){

		$('#datepicker12 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker12 a.ui-datepicker-prev').click();
	    		handlePrevClick12();
	    	}
	    });
	    $('#datepicker12 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker12 a.ui-datepicker-next').click();
	    		handleNextClick12();
	    	}
	    });


		function handlePrevClick12(){
			setTimeout(function () {
				triggerClick12();
			    $('#datepicker12').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick12(){
			setTimeout(function () {
				triggerClick12();
			    $('#datepicker12').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick12(){
			prev = $('#datepicker12').find('.ui-datepicker-prev');
			next = $('#datepicker12').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick12);
			$(prev).on('click', handlePrevClick12);
		}

		};



$(document).ready(function() {
var server = 'no';
if (server=='yes') {
	var firstLoad12 = true;
}else{
	var firstLoad12 = ($.fn.jquery.split('.')[1])<6?true:false;
}
var doe=[];
var load_doe12=function(y,m,callback){
  if(y.length!=4||m.length!=2) return;
  $.getJSON(window.location.protocol+'//'+window.location.host+'/calendar/doe/'+y+'/'+m, callback);
}
var proc_doe12=function(y,m,callback){
  var s=y+'-'+m;
  if(doe.indexOf(s)==-1){
    doe.push(s);
    load_doe12(y,m, function(data){
      if(data.code==200){
        for(key in data.response){
          var s=data.response[key].game_date;
          doe.indexOf(s)==-1&&doe.push(s);
        }
      }
      callback();
    });
  }else callback();
}

var cal_init12=function(){
  proc_doe12('2020','04',function(){
    $('#cal_loadimg12').hide();
    jQuery('#datepicker12').datepicker({
      prevText: ' ',
      nextText: ' ',
      onSelect: function(dateText, inst) {
//       	inst.inline = false;
        if (!firstLoad12) {
          var calendar_url = 'https://3riversconference.org/public/genie/20/school/4/';
          if(calendar_url.length<3)return;
          var selected_date = new Date(dateText);
          var additional_url = 'date/' +selected_date.getFullYear()+'-'+ (parseInt(selected_date.getMonth()) + 1) +'-' + selected_date.getDate() + '/view/day/';
calendar_url += additional_url;
          window.open(calendar_url,'_blank');
        }
        firstLoad12 = false;
      },
      onChangeMonthYear: function(y,m,obj){
        y+='';m=(m<10?'0':'')+m;
        doe.indexOf(y+'-'+m)==-1&&proc_doe12(y,m,function(){
          console.log(doe);
          jQuery('#datepicker12').datepicker();
        });
        setTimeout(function () {

		$('#datepicker12 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker12 a.ui-datepicker-prev').click();
	    		handlePrevClick12();
	    	}
	    });
	    $('#datepicker12 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker12 a.ui-datepicker-next').click();
	    		handleNextClick12();
	    	}
	    });


		function handlePrevClick12(){
			setTimeout(function () {
				triggerClick12();
			    $('#datepicker12').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick12(){
			setTimeout(function () {
				triggerClick12();
			    $('#datepicker12').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick12(){
			prev = $('#datepicker12').find('.ui-datepicker-prev');
			next = $('#datepicker12').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick12);
			$(prev).on('click', handlePrevClick12);
		}

        }, 50);
      },
      beforeShowDay : function(date) {
        var y=date.getFullYear()+'',m=date.getMonth()+1,d=date.getDate();
        m=(m<10?'0':'')+m;d=(d<10?'0':'')+d;
        return [ true, doe.indexOf(y+'-'+m+'-'+d)>0?'call-bold':'', '' ];
      },
    });
  });
}
