var doe=[];
var load_doe4=function(y,m,callback){
  if(y.length!=4||m.length!=2) return;
  $.getJSON(window.location.protocol+'//'+window.location.host+'/calendar/doe/'+y+'/'+m, callback);
}
var proc_doe4=function(y,m,callback){
  var s=y+'-'+m;
  if(doe.indexOf(s)==-1){
    doe.push(s);
    load_doe4(y,m, function(data){
      if(data.code==200){
        for(key in data.response){
          var s=data.response[key].game_date;
          doe.indexOf(s)==-1&&doe.push(s);
        }
      }
      callback();
    });
  }else callback();
}

var cal_init4=function(){
  proc_doe4('2020','04',function(){
    $('#cal_loadimg4').hide();
    jQuery('#datepicker4').datepicker({
      prevText: ' ',
      nextText: ' ',
      onSelect: function(dateText, inst) {
//       	inst.inline = false;
        if (!firstLoad4) {
          var calendar_url = ' ';
          if(calendar_url.length<3)return;
          var selected_date = new Date(dateText);
          var additional_url = 'ff_month_year='+ (parseInt(selected_date.getMonth()) + 1) +'-'+ selected_date.getFullYear() + '&ffDay=' + selected_date.getDate();
calendar_url += '&' + additional_url;
          window.open(calendar_url,'_blank');
        }
        firstLoad4 = false;
      },
      onChangeMonthYear: function(y,m,obj){
        y+='';m=(m<10?'0':'')+m;
