	    		$('#datepicker4 a.ui-datepicker-prev').click();
	    		handlePrevClick4();
	    	}
	    });
	    $('#datepicker4 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker4 a.ui-datepicker-next').click();
	    		handleNextClick4();
	    	}
	    });


		function handlePrevClick4(){
			setTimeout(function () {
				triggerClick4();
			    $('#datepicker4').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick4(){
			setTimeout(function () {
				triggerClick4();
			    $('#datepicker4').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick4(){
			prev = $('#datepicker4').find('.ui-datepicker-prev');
			next = $('#datepicker4').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick4);
			$(prev).on('click', handlePrevClick4);
		}

        }, 50);
      },
      beforeShowDay : function(date) {
        var y=date.getFullYear()+'',m=date.getMonth()+1,d=date.getDate();
        m=(m<10?'0':'')+m;d=(d<10?'0':'')+d;
        return [ true, doe.indexOf(y+'-'+m+'-'+d)>0?'call-bold':'', '' ];
      },
    });
