// Bug 76422

		window.addEventListener('load', myInit, true);

		function myInit(){

		$('#datepicker7 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker7 a.ui-datepicker-prev').click();
	    		handlePrevClick7();
	    	}
	    });
	    $('#datepicker7 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker7 a.ui-datepicker-next').click();
	    		handleNextClick7();
	    	}
	    });


		function handlePrevClick7(){
			setTimeout(function () {
				triggerClick7();
			    $('#datepicker7').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick7(){
			setTimeout(function () {
				triggerClick7();
			    $('#datepicker7').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick7(){
			prev = $('#datepicker7').find('.ui-datepicker-prev');
			next = $('#datepicker7').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick7);
			$(prev).on('click', handlePrevClick7);
		}

		};



