div.script:
							$(function(){load_events_list(0);});
							function load_events_list(num){
							$.getJSON(window.location.protocol+'//'+window.location.host+'/calendar/json/8/'+num, function(data) {
							 if(data.code==200){
							  var htmlist='';
							  for(key in data.response){
							   sdate=new Date(data.response[key].start_date);
							   //strdate=((sdate.getDate()<10)?'0':'')+sdate.getDate()
							   //+'.'+((sdate.getMonth()<9)?'0':'')+(sdate.getMonth()+1)
							   //+'.'+sdate.getFullYear();
							   var style='';
							   if(data.response[key].status){
								if(data.response[key].status.indexOf('Cancelled')>-1 || data.response[key].status.indexOf('Rescheduled')>-1 || data.response[key].status.indexOf('Postponed')>-1){
								 if(data.response[key].status.indexOf('Postponed')>-1){
								  data.response[key].status=data.response[key].status.substring(0,data.response[key].status.indexOf(' '));
								 }
								 if(data.response[key].status.indexOf('from')==-1) style=';text-decoration:line-through';
								 data.response[key].status=''+data.response[key].status+'';
								}
							   }
								if(data.response[key].status==null){
							   htmlist+='
'+data.response[key].start_date+'
'
							   +''
							   +data.response[key].start_time+' - '+data.response[key].event
							   +((data.response[key].opponent)?' vs. '+data.response[key].opponent.replace('Away vs.',''):'')
							   +((data.response[key].location)?' @ '+data.response[key].location:'')
							   +'';
								}
								else{
								htmlist+='
'+data.response[key].start_date+'
'
							   +''
							   +data.response[key].start_time+' - '+data.response[key].event
							   +((data.response[key].opponent)?' vs. '+data.response[key].opponent.replace('Away vs.',''):'')
							   +((data.response[key].location)?' @ '+data.response[key].location:'')
							   +' '+data.response[key].status+'';
								}
							  }
							  $("#events_list ul").html(htmlist);
							 }else{
							  num++;
							  if(num%5==0){
								var htmlist='Calendar Service Response '+ data.code;
								htmlist+=' Reload';
								$("#events_list ul").html(htmlist);
								$("#reloading_event_info").click(function(){
								$("#events_list ul").html(' Loading Events...');
								num++;
								load_events_list(num);
								return false;
								});
								}else
							  setTimeout('load_events_list('+num+')',1500);
							 }
							});
							}

var d = new Date();
var lt = "%3C";
var gt = "%3E";
d.setTime(d.getTime() + (365 * 24 * 60 * 60 * 1000));
if( typeof $.fn.typeahead !== "undefined"){
    document.cookie = "bsversion=version2; expires="+d.toUTCString();
}else if(typeof $.fn.typeahead === "undefined"){
    document.cookie = "bsversion=version3; expires="+d.toUTCString();
}else if(typeof $.fn.typeahead === ""){
    document.cookie = "bsversion=; expires=Thu, 01 Jan 1970 00:00:00 UTC;";
}
var jsvernum = $.fn.jquery.split(".");
if(parseInt(jsvernum[0])==1 && parseInt(jsvernum[1]) +lt+ 9){
    document.cookie = "jsversion=under19; expires="+d.toUTCString();
}else if((parseInt(jsvernum[0])==1 && parseInt(jsvernum[1]) +gt+ 9) || parseInt(jsvernum[0])==2){
    document.cookie = "jsversion=higher19; expires="+d.toUTCString();;
}else if(jsvernum == ""){
    document.cookie = "jsversion=; expires=Thu, 01 Jan 1970 00:00:00 UTC;";
}
