body.script:
$(document).ready(function (){
	$.each($('a'), function(idx, tag)
	{
		// you might make this smarter and throw out URLS like 
		// http://www.otherdomain.com/yourdomain.com
		if ($(tag).attr('href').indexOf('owatonna.k12.mn.us') > 0)
		{
			$(tag).attr('rel', 'external');
		} else if ($(tag).attr('href').indexOf('owatonna.k12.mn.us/') > 0)
		{
			$(tag).attr('rel', '');
		}
	});
	$('a[href][rel="external"]').click(
	  function () {
		window.open($(this).attr('href')); return false;
	  }
	);
	$("a[href^=http]").each(
    function(){
		if(this.href.indexOf(location.hostname) == -1) { 
        	$(this).attr('target', '_blank');
      	}
    }
  	);
});

