	function addToSchoolDropdown( where ){ //added by Jeremy - Case #00112468
		//The key will be the text displayed in the menu ( “Key”: “Value” )
	    //The value should be the URL the menu item should go to. 
	    var add = { 
	        "Early Childhood": "/domain/610"
	    } 

	    var more = ""; 

	    $.each(add, function(k, v){  //build the new schools to add to the list
	        more += '
' + k + ''; 
	    }); 
	        //Add them to the current list
	    $(where).append(more);
	    //Sorting Function
	    $.fn.sortList = function() {
	        var mylist = $(this);
	        var listitems = $('li:not(.rs-menu-option-sublist-header):not(.rs-menu-option-sublist-name)', mylist).get();
	        listitems.sort(function(a, b) {
	            var compA = $(a).text().toUpperCase();
	            var compB = $(b).text().toUpperCase();
	            return (compA < compB) ? -1 : 1;
	        });
	        $.each(listitems, function(i, itm) {
	            mylist.append(itm);
	        });
	    }

	        //sort the list A-Z
	    $(where).sortList();

	    var hideSchool = [
	    	'Academics',
	    	'StaffNet',
	    	'Student Services',
	    	'Community Ed',
	    	'Community Education'
	    ];

	    $('> li', where).each(function(k, v){
	    	if( hideSchool.indexOf($('> a', this).text().trim()) >= 0 ){
	    		$(this).remove();
	    	}
	    });
	}

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			multimediaRotator();
		}else{ //all else
			multimediaRotator();
		}
	}

	function multimediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
        	let gallerySize = {width: 1115, height: 401};

            if('Large' == 'Large'){
            	gallerySize.width = 1700;
                gallerySize.height = 600;
            }

			// MULTIMEDIA ROTATOR
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"              : gallerySize.width,
				"imageHeight"             : gallerySize.height,
				"appHeader"               : "no",
				"titles"                  : "yes",
				"captions"                : "yes",
				"galleryOverlay"	  : "no",
				"linkedElement"		  : ["image"], // ["image", "title", "overlay", "all"]
				"playPause"               : "no",
				"mainControls"            : "yes",
				"contMoreLink"            : "no",
				"contMoreLinkText"        : "Read More",
				"descMoreLink"            : "yes",
				"descMoreLinkText"        : "MORE",
				"bullets"                 : "yes",
				"bulletControls"          : "no",
		 		"thumbnails"              : "no",
				"thumbnailWidth"          : 122,
				"thumbnailHeight"         : 53,
				"thumbnailSlideDirection" : "horizontal", // horizontal or vertical
				"hoverPause"              : "yes",
		                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
				"fullScreenVideo"         : "no",
				"transitionType"          : "fade", // fade or slide
				"transitionSpeed"         : 3,
				"transitionDelay"         : 8,
				"onImageLoad"             : function() { },
				"allImagesLoaded"         : function() { },
				"onThumbnailLoad"         : function() { },
				"allThumbnailsLoaded"     : function() { },
				"onTransitionStart"       : function() { },
				"onTransitionEnd"         : function() { },
				"watchVidClick"           : function() { },
				"closeVidClick"           : function() { },
				"allLoaded"               : function() { 
					if( $('#hp-slideshow .rs-gallery-control').length > 0 ){
						$('#hp-slideshow .rs-gallery-control').addClass("icon-font");
                        $('#hp-slideshow .rs-gallery-images').before($('#hp-slideshow .rs-gallery-control.back'));
					}

					$('#hp-slideshow .rs-gallery-desc').after($('#hp-slideshow .rs-gallery-bullets-outer'));
					//$("#hp-slideshow .rs-gallery-control").appendTo("#hp-slideshow");
				}
			});
		}
		if(!$("#sw-content-container11.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			$("#sw-content-container11.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaCardRotator({
				"imageWidth"              : 266,
				"imageHeight"             : 184,
				"appHeader"               : "no",
				"titles"                  : "no",
				"captions"                : "no",
				"galleryOverlay"	  : "no",
				"linkedElement"		  : [], // ["image", "title", "overlay", "all"]
				"playPause"               : "no",
				"mainControls"            : "yes",
