				"onImageLoad"             : function() { },
				"allImagesLoaded"         : function() { },
				"onThumbnailLoad"         : function() { },
				"allThumbnailsLoaded"     : function() { },
				"onTransitionStart"       : function() { },
				"onTransitionEnd"         : function() { 
                	$('#hp-footer-slideshow .rs-card-desc a').attr('tabindex', -1);
                },
				"watchVidClick"           : function() { },
				"closeVidClick"           : function() { },
				"allLoaded"               : function() { 
                	$('#hp-footer-slideshow .rs-card-desc').css('opacity', 0).show();

					if( $('#hp-footer-slideshow .rs-gallery-control').length > 0 ){
						$('#hp-footer-slideshow .rs-gallery-control').addClass("icon-font");

                        $('#hp-footer-slideshow .rs-gallery-images').before($('#hp-footer-slideshow .rs-gallery-control.back'));
					}

					$('#hp-footer-slideshow .rs-gallery-images ul.rs-gallery-images-list li').hover(
						function(e){ //in
							galleryCardControl('show', this);
						},
						function(e){  //out
							galleryCardControl('hide', this);
						}
					);

                    $('#hp-footer-slideshow .rs-card-desc').focus(function(){
                        var li = $(this).parent();
                        galleryCardControl('hideAll')
                        galleryCardControl('show', li);

                    })
                    .blur(function(){
                        var li = $(this).parent();
                        //galleryCardControl('hide', li);
                    });

					$('#hp-footer-slideshow .rs-gallery-images ul.rs-gallery-images-list li').each(function(k, v){
						if( $('.rs-card-desc', this).html() == "" ){
							$('.rs-card-desc', this).remove();
						}
					});
				}
			});
		}
	}

    function galleryCardControl(action, li){
    	switch(action){
        	case "show":
            	$('.rs-card-desc', li)
                	.stop()
                	.hide()
                    .css('opacity', 1)
                    .slideDown(500);
                $('.rs-card-desc a', li).attr('tabindex', 0);
				$('.rs-card-base-title', li).stop().slideUp(50);
            	break;

            case "hide":
            	$('.rs-card-desc a', li).attr('tabindex', -1);
            	$('.rs-card-desc', li).slideUp(500, function(){
                	$(this).css('opacity', 0).show();
                });
                $('.rs-card-base-title', li).slideDown(480);
            	break;

            case "hideAll":
            	$('#hp-footer-slideshow li.active .rs-card-desc a').attr('tabindex', -1);
            	$('#hp-footer-slideshow li.active .rs-card-desc').slideUp(0).css('opacity', 0).show();
                $('#hp-footer-slideshow li.active .rs-card-base-title').slideDown(0);
            	break;
        }
    }

	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "no",
	            dayWeek     : "yes"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
			    var moveArticle = $(this).html();
			    $(this).parent().prev().children().children().next().append(moveArticle);
			    $(this).parent().remove();
			};
		});
		$(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var dayName = newDateTime[0] + '';

			newDateTime = newDateTime[1] + '' + newDateTime[2] + '';

			$('.upcoming-column.left h1', this).html(newDateTime);	

			$('.upcoming-column.right', this).prepend(dayName);
		});
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#hp-content div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}

		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//DOC accordion header click
		$("#hp-content div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("#hp-content div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

