link.script:
if (typeof RokSprocket == 'undefined') RokSprocket = {};
Object.merge(RokSprocket, {
	SiteURL: 'https://www.isd100.org',
	CurrentURL: 'https://www.isd100.org',
	AjaxURL: 'https://www.isd100.org/wp-admin/admin-ajax.php?action=roksprocket_fe'
});

window.addEvent('domready', function(){
	RokSprocket.instances.strips.attach(4, '{\"animation\":\"fadeDelay\",\"autoplay\":\"0\",\"delay\":\"5\"}');
});
window.addEvent('load', function(){
   var overridden = false;
   if (!overridden && window.G5 && window.G5.offcanvas){
       var mod = document.getElement('[data-strips=\"4\"]');
       mod.addEvents({
           touchstart: function(){ window.G5.offcanvas.detach(); },
           touchend: function(){ window.G5.offcanvas.attach(); }
       });
       overridden = true;
   };
});

