jQuery(document).ready(function($) {
	$(".showhide-content").hide();
	$(".showhide").append(' (open)').css({'cursor': 'pointer'});
	
	$('.showhide').click(function(e){
		var link = $(this);
		var span = link.find('span');
		link.next('.showhide-content').slideToggle(500, function() {
			if ($(this).is(":visible")) {
				 span.removeClass('open').addClass('close').text('(close)');                
			} else {
				 span.removeClass('close').addClass('open').text('(open)');                
			}        
		});
		e.preventDefault();
	});
});
