link.script:
jQuery(document).ready(function() {
	// Hide all the divs when page loads
	for(var i = 1; i <= 25; i++) {
	    jQuery('#reveal_' + i).hide();
	}
	jQuery('.rvButton_1').click(function(e){
		e.preventDefault();
		var buttonNumber = 1
		// Hide all the divs
		for(var i = 1; i <= 25; i++) {
			if( i == buttonNumber) continue;
		jQuery('#reveal_' + i).hide();
		}	
		jQuery('#reveal_' + buttonNumber).slideToggle();
	});
	jQuery('.rvButton_2').click(function(e){
		e.preventDefault();
		var buttonNumber = 2
		// Hide all the divs
		for(var i = 1; i <= 25; i++) {
			if( i == buttonNumber) continue;
		jQuery('#reveal_' + i).hide();
		}	
		jQuery('#reveal_' + buttonNumber).slideToggle();
	});
	jQuery('.rvButton_3').click(function(e){
		e.preventDefault();
		var buttonNumber = 3
		// Hide all the divs
		for(var i = 1; i <= 25; i++) {
			if( i == buttonNumber) continue;
		jQuery('#reveal_' + i).hide();
		}
		jQuery('#reveal_' + buttonNumber).slideToggle();	
	});
	jQuery('.rvButton_4').click(function(e){
		e.preventDefault();
		var buttonNumber = 4
		// Hide all the divs
		for(var i = 1; i <= 25; i++) {
			if( i == buttonNumber) continue;
		jQuery('#reveal_' + i).hide();
		}	
		jQuery('#reveal_' + buttonNumber).slideToggle();
	});
});
