div.script:
$(".group850000").colorbox({rel:'group850000', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group850000").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery850000 = $("a[rel=group850000]").colorbox({maxWidth: "80%"});
$("a#openGallery850000").click(function(e){
    e.preventDefault();
    $gallery850000.eq(0).click();
});

