;(function($) {
  'use strict';
  $(function() {
    // Wait until all of the images in the features are loaded
    $('.socs-feature-img').imagesLoaded(function() {
      $('.socs-feature.two').each(function() {
        var $this = $(this);

        // Add a border on the side of whichever box is the tallest - .two or .three
        if ($this.height() >= $this.next().height()) {
          $this.addClass('bordered');
        } else {
          $this.next().addClass('bordered');
        }
      });

      // If the last feature is the left-side of the "split" - undo the split
      var $lastFeature = $('.socs-feature:last');
      if ($lastFeature.hasClass('two')) {
        $lastFeature.css({
          'float': 'none',
          'padding-right': '0',
          'width': 'auto'
        }).removeClass('bordered');
      }
    });

  });
})(jQuery);

(function($){
  $(function() {

    var divH = 0, maxHeight = 0;

    $('#calendarWeek td div:has(ul)').each(function() {
      var $div = $(this);
      var ulH = $div.find('ul').height();
      divH = $div.height();

      // Get the tallest  in the group
      if (ulH > maxHeight) maxHeight = ulH;

      // If the  is taller than the 
...
      if (ulH > divH) {
        // Which  is this 
 in?
        var tdCount = $div.parent().prevAll().length + 1;

        // Find the same number  in the "more" row
        var $tdMore = $('#calendarWeek tr.more td:nth-child(' + tdCount + ')');

        // Add a "More..." link
        $tdMore.append('More...<\/a>');
      }
    });

    var $uls = $('#calendarWeek td div ul');
    var $links = $('#calendarWeek tr.more td a');
    $links.click(function() {
      var $this = $(this);
      if ($this.text() === 'More...') {
        $uls.each(function() {
          // Animate a "slide down" effect on the parent div's height
          $(this).parent().animate({'height': maxHeight});
        });
        $links.text('...Less');
      } else {
        $uls.each(function() {
          // Animate a "slide up" effect to the parent div's original height
          $(this).parent().animate({'height': divH});
        });
        $links.text('More...');
      }
      $this.blur(); // Lose focus to get rid of the outline
      return false;
    });

  });
})(jQuery);

;(function($) {
  var href = window.location.href;
  if (href.indexOf('?') === -1) {
    href += '?template=m';
  } else {
    href += '&template=m';
  }
  $('#copyRight .mobileSite').attr('href', href).click(function() {
    // Reset the session cookie to redirect to mobile.
    $.cookie('socs_MobileSwitch', 'y', { path: '/' });
  });
})(jQuery);
