div.script:
// The "Load More" portion of the page - yay infinite scroll!
var countby = 50;		// How many to pull at each clip
var startwith = 0;		// What we start at after the first load
var keepgoing = true;	// Keep loading more results (we haven't hit the end
var doneprocess = true;	// True if we're done processing, false if we're loading currently.

function showFeed(data) {
	// We're done processing
	doneprocess = true;
	$('.loadimage').hide();

	// Update the startat variable with the next set we'll want to get
	startwith = startwith + countby;

	// Check to see if we have no results - that's all folks!
	if (data.length == 0) {
		keepgoing = false;
		var new_element = '

 
That\'s all folks!';
		$(new_element).hide().appendTo('#thefeed').fadeIn();
		return;
	}

	// Make sure weird stuff doesn't happen with scrolling
	$('#feedcontents').scrollTop($('#feedcontents').scrollTop()-1);

	// Loop through each post
	for (var i=0; i < data.length; i++) {
		// Store the post to a variable
		var new_element = 
'	
' + 
'		' +
			(data[i].pinned > 0 ? '			' : '') +
'			' +
'			
' + data[i].postedby + '' +
'			
' + data[i].datetime + '' +
'		' +
'		';
		// Tag processing
        if (data[i].tags) {
            var tags = data[i].tags.split(',');
            for (var j=0; j < tags.length; j++) {
                new_element += '			
' + tags[j] + '';
            }
        }
		// Resume
		new_element += 
'		' +
'		
' +
'			' + data[i].text + '';

		// Process Attachments and Media
		for (var j = 0; j < data[i].am.length; j++) {
            if (data[i].am[j].type == 'img') {
                new_element += '		';
            }
            if (data[i].am[j].type == 'pdf') {
                new_element += 
'			' +
'				' +
'			';
            }
            if (data[i].am[j].type == 'url') {
                new_element += 
'			' +
'				' +
'			';
            }
		}

		// And now return to our normal programming
		new_element +=
'		' +
'	';
		$(new_element).hide().appendTo('#thefeed').fadeIn();

	}
}

$('#feedcontents').scroll(function() {
	var end = $("#thebottom").offset().top;
	var viewEnd = $('#feedcontents').scrollTop() + $('#feedcontents').height();
	var distance = end - viewEnd;
	if (doneprocess && keepgoing && distance < 500) {
		doneprocess = false;
		$('.loadimage').fadeIn();
		$.post('/lib/ajax/load_moreposts.php', {
			'startwith': startwith,
            'countby': countby,
            'school': 'do'
		}, showFeed);
    }
});

// Initial page load
$.post('/lib/ajax/load_moreposts.php', {
    'startwith': startwith,
    'countby': countby,
    'school': 'do'
}, showFeed);

input.script:
$(function() {
	$("#notificationEmailEntry").dialog({
		autoOpen: false,
		buttons: [ {
			text: "Next",
			click: function() {
				$('#nsuEmailAddress').val( $('#dlgEmailAddress').val() );
				$('#nsuButton').trigger('click');
				$(this).dialog('close');
			}
		} ],
		modal: true,
		resizable: true,
		width: 600
	});
	$("#backpackNotificationError").dialog({
		autoOpen: false,
		buttons: [ {
			text: "I'll Try Again",
			click: function() {
				$(this).dialog( "close" );
			}
		} ],
		modal: true,
		resizable: true,
		width: 600
	});
	$("#backpackNotificationSchools").dialog({
		autoOpen: false,
		buttons: [ {
			text: "Sign Up",
			icons: {
				primary: "ui-icon-check"
			},
			click: function() {
				$(this).dialog( "close" );
				$("#loading").dialog("open");
				var res_backpack = [];
				var res_school_cal = [];
				var res_athletic_cal = [];
				$('input:checkbox:checked', this ).each(function() {
					var index = $( this ).val();
					if ($(this).hasClass('backpack')) {
						res_backpack.push( index );
					} else if ($(this).hasClass('school_cal')) {
						res_school_cal.push( index );
					} else if ($(this).hasClass('athletic_cal')) {
						res_athletic_cal.push( index );
					}
				});
				$.post('/lib/ajax/notify_saveemail.php', {
					'email':	$("#nsuEmailAddress").val(),
					'result':	{ 'backpack': res_backpack, 'school_cal': res_school_cal, 'athletic_cal': res_athletic_cal }
				}, function(data) {
					// Success!
					if (data.status == 'success') {
						$("
").html('
Thank you!
Your preferences have been saved!').dialog({resizable: false,modal: true,title: "Notification Sign Up",});
					} else {
						$("
").html('
Uh oh....
Well, something didn\'t work.  Try again, and if it still doesn\'t work, let the folks at the Technology Office know about this.
Give them the following message:
' + data.message + '').dialog({resizable: false,modal: true,title: "Error",dialogClass: "ui-state-error"});
					}
				}, 'json').fail(function() {
					//Fail :o[
					$("
").html('
Uh oh....
Well, something didn\'t work.  Try again, and if it still doesn\'t work, let the folks at the Technology Office know about this.
They can probably fix it.').dialog({resizable: false,modal: true,title: "Error",dialogClass: "ui-state-error"});
				}).always(function() {
					$("#loading").dialog("close");
				});
			}
		}, {
			text: "Cancel",
			click: function() {
				$(this).dialog( "close" );
			}
		} ],
		modal: true,
		resizable: true,
		height: 700,
		width: 600
	});
	$("#nsuButton").click(function() {
		// Reset the notification text
		$("#backpackNotificationSchools h2").html("Wait! We have one quick question...");
		$("#backpackNotificationSchools #alertText").html("What do you want to receive email notifications for?  You can select as many as you'd like.");
		$("#backpackNotificationSchools input:checkbox").prop('checked', false);

		if ($("#nsuEmailAddress").val() == "") {
			$("#errorText").html("It looks like you forgot to enter your email address.  Click the button below and lets try it again.  Enter your email address where it says Email Address and then click the Sign Up button.");
			$("#backpackNotificationError").dialog( "open" );
			return;
		}

		if (validateEmail($("#nsuEmailAddress").val()) == false) {
			$("#errorText").html("It looks like you didn't enter a valid email address.  Click the button below and lets try it again.  Enter your correct email address where it says Email Address and then click the Sign Up button.");
			$("#backpackNotificationError").dialog( "open" );
			return;
		}

		$("#loading").dialog("open");

		$.ajax({
			url: "/lib/ajax/notify_email.php",
			type: "POST",
			data: {"email": $("#nsuEmailAddress").val()},
			dataType: "json"
		}).done(function(data) {
			if (data['school'].length != 0) {
				$.each(data['school'], function(key, val) {
					$("#backpackNotificationSchools #"+val).prop('checked', true);
				});
				$("#backpackNotificationSchools h2").html("Adjust Your Notifications");
				$("#backpackNotificationSchools #alertText").html("Hey, it looks like you've already signed up to our notifications using the email address "+$("#nsuEmailAddress").val()+".  Adjust your preferences and click 'Sign Up' to save your changes.");
			}
			$("#backpackNotificationSchools #backpack_").prop('checked', true);
			$("#backpackNotificationSchools #school_cal_").prop('checked', true);
		}).always(function() {
			$("#loading").dialog("close");
			$("#backpackNotificationSchools").dialog( "open" );
		});
	});

	$('#notifytabs').tabs();

	function validateEmail(email) {
		var re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
		return re.test(email);
	}
});
