 function liclick(navid)
{
	if(navid == "1")
		{
			var stylevalue=document.getElementById('DISTRICT').style.display;
			if(stylevalue=="")
			{
			document.getElementById('DISTRICT').style.display='none';
			}
			if(stylevalue=="block")
			{
			document.getElementById('DISTRICT').style.display='none';
			}
			if(stylevalue=="none")
			{
			document.getElementById('DISTRICT').style.display='block';
			}
		}

		if(navid == "2")
		{
			var stylevalue=document.getElementById('STAFF').style.display;
			if(stylevalue=="")
			{
			document.getElementById('STAFF').style.display='none';
			}
			if(stylevalue=="block")
			{
			document.getElementById('STAFF').style.display='none';
			}
			if(stylevalue=="none")
			{
			document.getElementById('STAFF').style.display='block';
			}
		}


		if(navid == "3")
		{
			var stylevalue=document.getElementById('CALENDAR').style.display;
			if(stylevalue=="")
			{
			document.getElementById('CALENDAR').style.display='none';
			}
			if(stylevalue=="block")
			{
			document.getElementById('CALENDAR').style.display='none';
			}
			if(stylevalue=="none")
			{
			document.getElementById('CALENDAR').style.display='block';
			}
		}



}	



