link.script:
$(document).ready(function() {

	/* START */
	var id = '#dialog';

//Get the screen height and width
var maskHeight = $(document).height();
var maskWidth = $(window).width();

//Set heigth and width to mask to fill up the whole screen
$('#mask').css({'width':maskWidth,'height':maskHeight});

//transition effect
$('#mask').fadeIn(500);	
$('#mask').fadeTo("slow",0.9);	

//Get the window height and width
var winH = $(window).height();
var winW = $(window).width();

var dvh=winH/2-$(id).height()/2;

if(dvh>0) {

	var dvhht=winH/2-$(id).height()/2;

} else {

	var dvhht=2;
}

//Set the popup window to center
$(id).css('top',  dvhht);
$(id).css('left', winW/2-$(id).width()/2);

//transition effect
$(id).fadeIn(2000); 	

//if close button is clicked
$('.window .close').click(function (e) {
//Cancel the link behavior
e.preventDefault();

$('#mask').hide();
$('.window').hide();
});

//if mask is clicked
$('#mask').click(function () {
$(this).hide();
$('.window').hide();
});

/* END */

    // Configure/customize these variables.
    var showChar = 150;  // How many characters are shown by default
    var ellipsestext = "...";
    var moretext = "Read more >";
    var lesstext = "Read less";


    $('.more').each(function() {
        var content = $(this).html();

        if(content.length > showChar) {

            var c = content.substr(0, showChar);
            var h = content.substr(showChar, content.length - showChar);

            var html = c + '' + ellipsestext+ ' ' + h + '  ' + moretext + '';

            $(this).html(html);
        }

    });

    $(".morelink").click(function(){
        if($(this).hasClass("less")) {
            $(this).removeClass("less");
            $(this).html(moretext);
        } else {
            $(this).addClass("less");
            $(this).html(lesstext);
        }
        $(this).parent().prev().toggle();
        $(this).prev().toggle();
        return false;
    });
});


function searchstaff()
{

	var staff=document.getElementById('stafflist').value;
	var filename='index';
	var lastname=document.getElementById('stafflastname').value;

	$.ajax({  
	type: "get",  
	url: "searchstaff",  
	data: {staff: staff,filename: filename,lastname: lastname},
	beforeSend: function() {

	$('html, body').animate({scrollBottom:0}, 'slow');	
	},  
	success: function(response)	{	    		

document.getElementById('staffresp').innerHTML='';
document.getElementById('staffresp').innerHTML=response;


	}

});	 

}

