jQuery(window).load(function () {

setTimeout(function () {



	var x = document.getElementById('innerScrollArea');

	if (x.style.display === 'none') {

		x.style.display = 'block';	

		//document.getElementById('imgone').style.display = 'block';	
		//document.getElementById('imgtwo').style.display = 'block';	

		document.getElementById("scroller").style.height = 495+'px';

	}
        var scroller = $('#scroller div.innerScrollArea');
        var scrollerContent = scroller.children('ul');
        scrollerContent.children().clone().appendTo(scrollerContent);
        var curX = 0;
        scrollerContent.children().each(function(){
            var $this = $(this);
            $this.css('left', curX);
            curX += $this.width();
        });
        var fullW = curX / 2;
        var viewportW = scroller.width();

        // Scrolling speed management
        var controller = {curSpeed:0, fullSpeed:2};
        var $controller = $(controller);
        var tweenToNewSpeed = function(newSpeed, duration)
        {
            if (duration === undefined)
                duration = 500;
            $controller.stop(true).animate({curSpeed:newSpeed}, duration);
        };

        // Scrolling management; start the automatical scrolling
        var doScroll = function()
        {
            var curX = scroller.scrollLeft();
            var newX = curX + controller.curSpeed;
            if (newX > fullW*2 - viewportW)
                newX -= fullW;
            scroller.scrollLeft(newX);
        };
        setInterval(doScroll, 19);
        tweenToNewSpeed(controller.fullSpeed);

	}, 10);	

	 function countDown(){
        seconds--
        $("#seconds").text(seconds);
        if (seconds === 0){


          clearInterval(i);
        }
      }

      var seconds = 1,
          i = setInterval(countDown, 2000);
});

