link.script:
                $(document).ready(function () {
                    $('time[data-utc-time]').each(
                        function () {
                            var utcTimeStr = $(this).attr('data-utc-time');
                            var localTime = moment.utc(utcTimeStr).toDate();

                            $(this).attr('datetime', moment(localTime).format('YYYY-MM-DD HH:mm:ss'));
                            $(this).find('.event-month').text(moment(localTime).format('MMM'));
                            $(this).find('.event-day-number').text(moment(localTime).format('D'));

                            if ($(this).hasClass('event-time')) {
                                $(this).text(moment(localTime).format('h:mm a'));
                            }
                        }
                    );
                });

