            });

            //Copy social media icons to mystart area
            $(function(){
            	var $socialMystart = $('.gb.footer.six').html();
                $socialMystart = $socialMystart.substring($socialMystart.indexOf("") + 3);
                //$socialMystart = '
' + $socialMystart + ''
                $socialMystart = $socialMystart.replace(/<a /gi, '<a style="margin-top: 0px; margin-right: 10px; margin-bottom: 4px;" ');
                $socialMystart = $socialMystart.replace(/<a/gi, '
