   function getQueryVariable(variable) {
	var query = window.location.search.substring(1);
	var vars = query.split("&");
	for (var i=0;i= 320 && $(window).width() <= 639){
			calendarListView();
		}
		if(!isMSIE){ imgResize(); }
		searchText();

		if($("#sw-content-container2.ui-hp .tabs-container").length) {
			/*$("#sw-content-container2.ui-hp div.ui-widget.app").each(function() {
				var thisApp = $(this);
				if(!$("a.more-link").length || !$("a.ui-btn-toolbar.rss").length) {
					$(".ui-widget-detail",thisApp).css("padding-top","46px");
				}
			});*/
			$("#sw-content-container2.ui-hp div.ui-widget.app").not(".headlines").css("padding-top","20px");
		}

		$(".sw-channel-dropdown").each(function() {
			var e = $(this);
			if(!$("li",this).length) {
				$(e).remove();
			}
		});

        megaMenuDropdown();

	});

	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE7){ $("body").addClass("msie7"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ pgScreenSize(); imgResize(); });

		// RESPONSIVE MENU
		var gbIconsMenu = '1,Google Apps,https://googleapps.chatham-nj.org,_blank;' +
				  '1,Parent Portal,https://parents.chatham-nj.org,_blank;' +
