				$(this).find(".sw-channel-dropdown").show();
			} else {
				$(".cs-mega-dropdown.channel-one").clone().appendTo(this);
				$(j).css({"position":"static"});
				$(j).parent().parent().css("position","relative");

				var getChannelName = $(j).find("> a span").text();
				var getChannelDropdown = $(j).find(".sw-channel-dropdown .mega-column.four");
				if(!$(".channel-name-header",j).length) { 
					$("
"+ getChannelName +"").prependTo(getChannelDropdown);
				}

				if(!$("li.sw-channel-item:eq(1) .mega-column.four .cloned-channel-dropdown").length) {
					$("li.sw-channel-item:eq(1) > .sw-channel-dropdown").addClass("hide-system-dropdown");
					$("li.sw-channel-item:eq(1) > .sw-channel-dropdown").clone().removeClass("hide-system-dropdown").addClass("cloned-channel-dropdown").prependTo("li.sw-channel-item:eq(1) .mega-column.four");
					$("li.sw-channel-item:eq(1) .mega-column.four .cloned-channel-dropdown:eq(0)").show();
				} else {
					$("li.sw-channel-item:eq(1) .mega-column.four .sw-channel-dropdown:eq(0)").show();
				}

				$(".cs-mega-dropdown.channel-one",j).show().css("z-index","7000");
			}
		}, function() {
			$(this).css("position","relative");
			$(".cs-mega-dropdown",this).remove();
		});
