					$("li.sw-channel-item:eq(4) > .sw-channel-dropdown").addClass("hide-system-dropdown");
					$("li.sw-channel-item:eq(4) > .sw-channel-dropdown").clone().removeClass("hide-system-dropdown").addClass("cloned-channel-dropdown").prependTo("li.sw-channel-item:eq(4) .mega-column.four");
					$("li.sw-channel-item:eq(4) .mega-column.four .cloned-channel-dropdown:eq(0)").show();
				} else {
					$("li.sw-channel-item:eq(4) .mega-column.four .sw-channel-dropdown:eq(0)").show();
				}

				$(".cs-mega-dropdown.channel-four",j).show().css("z-index","7000");
			}
		}, function() {
			$(this).css("position","relative");
			$(".cs-mega-dropdown",this).remove();
		});
