var style18366 = "";
    $(document).ready(function () {

        createTableView18366(18366, "", "");
        
        windowresize18366();
    });

	$(window).resize( function () {
      windowresize18366();
    });

	function isTable18366Responsive() {
    	var contentBreakPoint18366 = 600;
        
        if ('' != '') {
        	contentBreakPoint18366 = '';
            
        }
       
    	var screenWidth = (window.innerWidth > 0) ? window.innerWidth : screen.width;
		var contentWidth18366 = $("#tableApp18366").width();
        // Jason S - changed to trigger responsive for any screen size under 1024px 
        var deviceBool18366 = (screenWidth < 1024);
		
        if ( deviceBool18366 || contentWidth18366 < contentBreakPoint18366 ) {
        	return true;
        } else {
        	return false;
        }
    }
    
	function windowresize18366() {
		// trigger resposive if < 1024 screen size or content size below 600 - used for table app in multiple column layouts
        if ( isTable18366Responsive() ) {
            $("#tableApp18366").addClass("triggerResponsive");
            addResponsiveStyle18366();
        } else {
            $("#tableApp18366").removeClass("triggerResponsive");
            $("#tableapp-responsivestyle-tableApp18366").remove();
        } 
    }
    
    function addResponsiveStyle18366() {
        if ($("#tableapp-responsivestyle-tableApp18366").length == 0 && style18366 != "") {
    		// Jason S. - changed max width to 1024 to prevent losing headings when responsiveness is triggered SCB-7487
        	$('head').append("");
        }
    }

    function createTableView18366(id, hasHeaderRow, hasHeaderCol) {
		
        if ($("#hidData" + id).val() != "") {
            var tableId = "tableApp" + id
            var data = JSON.parse($("#hidData" + id).val());
            var classNames = JSON.parse($("#hidClassNames" + id).val());

            var headeHtml = "";
            var bodyHtml = "";
            var height = "auto";
            var width = "auto";
            var rowTitle = [];
            var columnTitle = [];
            style18366 = " caption {display: block;}.tableAppHead {position: absolute;top: -9999px;left: -9999px;}";
            $.each(data, function (row, rowItem) {
                if (hasHeaderRow == "True" && row == 0) {
                    headeHtml += "
";
                    $.each(rowItem, function (column, columnItem) {
                        var className = "";
                        if (classNames.length > row && classNames[row].length > column) {
                            className = classNames[row][column];
                        }
                        headeHtml += "
" + (columnItem == null || columnItem == "" ? " " : columnItem) + "";
                        rowTitle.push(columnItem == null ? " " : columnItem);
                    });
                    headeHtml += "";
                } else {
                    bodyHtml += "
";
                    $.each(rowItem, function (column, columnItem) {
                        var className = "";
                        if (classNames.length > row && classNames[row].length > column) {
                            className = classNames[row][column];
                        }
                        if (hasHeaderCol == "True" && column == 0) {
                            bodyHtml += "
" + (columnItem == null || columnItem == "" ? " " : columnItem) + "";
                            columnTitle.push(columnItem == null ? " " : columnItem);
                        } else {
                            bodyHtml += "
" + (columnItem == null || columnItem == "" ? " " : columnItem) + "";
                        }
                    });
                    bodyHtml += "";
                }
            });
            $("#" + tableId + " thead ").html(headeHtml);
            $("#" + tableId + " tbody ").html(bodyHtml);
            $.each(rowTitle, function (index, item) {
                if (hasHeaderCol == "True" && hasHeaderRow == "True") {
                    if (index != 0) {
                        style18366 += "#" + tableId + " td:nth-of-type(" + index + "):before { content: '" + $("
"+item+"").text() + "'; }";
                    }
                } else {

                    style18366 += "#" + tableId + " td:nth-of-type(" + (index + 1) + "):before { content: '" + $("
" + item + "").text() + "'; }";

                }


            });
            if (hasHeaderRow == "False" && hasHeaderCol == "True") {
                $.each(columnTitle, function (index, item) {
                    $("#" + tableId + " tbody tr").eq(index).prepend($("" + $("
" + item + "").text() + ""));
                });
            }
            
            if (isTable18366Responsive()) {
            	addResponsiveStyle18366();
            }
            
        }
    }

