    $("#circle-delete").click(function(){
        //$("#mobile-search-box").hide();
        $("#mobile-search-box").width(0);
    });
}

function ShowSubcategoryMenu(){
    if(!$("#menuarea-wrapper").html()) {
        $("#menuarea-wrapper").load("/include/srn_lib/psr/html_fragments/ajax_header_subcategory.php",
            function(responseTxt, statusTxt, xhr) {
                if(statusTxt=="success") {
                    HoverOnSubcategoryMenu();
                }
            }
        );
    }else{
        HoverOnSubcategoryMenu();
    }
}

function ResponsiveTooltips(){
	var targets = $("[rel~=tooltip]"),
	target  = false,
	tooltip = false,
	title   = false;
	targets.bind("mouseenter", function(){
		target=$( this );
		tip=target.attr("title");
		title=target.attr("title");
		tooltip=$("
");
		if(!tip || tip == "" )return false;
		target.removeAttr("title");
		tooltip.css("opacity", 0).html(tip).appendTo("body");
		var init_tooltip = function(){
			if( $( window ).width() < tooltip.outerWidth() * 1.5 )
				tooltip.css("max-width", $( window ).width() / 2 );
			else
				tooltip.css("max-width", 340 );
			var pos_left = target.offset().left + ( target.outerWidth() / 2 ) - ( tooltip.outerWidth() / 2 ),
			pos_top  = target.offset().top - tooltip.outerHeight() - 20;
			if( pos_left < 0){
				pos_left = target.offset().left + target.outerWidth() / 2 - 20;
				tooltip.addClass("left");
			}else{
				tooltip.removeClass("left");
			}
			if( pos_left + tooltip.outerWidth() > $( window ).width()){
				pos_left = target.offset().left - tooltip.outerWidth() + target.outerWidth() / 2 + 20;
				tooltip.addClass("right");
			}else{
				tooltip.removeClass("right");
			}
			if( pos_top < 0 ){
				var pos_top  = target.offset().top + target.outerHeight();
				tooltip.addClass("top");
			}else{
				tooltip.removeClass("top");
			}
			tooltip.css({ left: pos_left, top: pos_top }).animate( { top: "+=10", opacity: 1 }, 50 );
		};
		init_tooltip();
		$( window ).resize(init_tooltip);
		var remove_tooltip = function(){
			tooltip.animate( { top: "-=10", opacity: 0 }, 50, function(){
				$( this ).remove();
			});
			target.attr("title", title);
		};
		target.bind("mouseleave", remove_tooltip );
		tooltip.bind("click", remove_tooltip );
	});
}

 function LoadAjaxContents(tab){
    var baseUrl='/new-mexico/grants-cibola-county-schools-school-district/3501170-school-district';
    var ajaxRequestUrl=baseUrl+'/'+'tab'+'/'+tab;
    if(tab!='all'){
        var pushUrl=baseUrl+'/'+tab;
    }else{
        var pushUrl=baseUrl;
    }
    if(window["loadAjaxSchoolMarkers"] && map){
        if(typeof map=="object" && map.hasOwnProperty('_container') && map._container.hasOwnProperty('_leaflet')){
            map.remove();
        }
    }
    $.ajax({
        url:ajaxRequestUrl,
        type:"GET",
        dataType: "html",
        cache:true,
        success:function(data){
            var $data = $(data);
            var title = $data.find(".ajax_title").html();
            var meta_description = $data.find(".ajax_meta_description").html();
            var custom_text = $data.find(".ajax_custom_text").html();
            var head_line =  $data.find(".ajax_head_line").html();
            var table_title =$data.find(".ajax_table_title").html();
            var table_list = $data.find(".ajax_table_list").html();
            var schoo_list_first=$data.find(".ajax_table_list #schoo-list-first").html();
            var schoo_list_second=$data.find(".ajax_table_list #schoo-list-second").html();
            var mapSetting = $data.find(".ajax_map_setting").html();
            var callBack = $data.find(".ajax_map_callback").html();
            var callBack=$.trim(callBack);

            if(title){
                $("title").html(title);
            }
            if(meta_description){
                $("meta[name=description]").attr("content",meta_description);
            }
            if(custom_text){
                if($(".school_notes_overview").length){
                    $(".school_notes_overview").html(custom_text);
                }
                if($(".tab-custom-text-cnt").length){
                    $(".tab-custom-text-cnt").html(custom_text);
                }
            }
            if(head_line){
                if($("h1#main-headline").length){
                    $("h1#main-headline").html(head_line);
                }
            }
            if(table_title){
                if($("h2.table-open-title").length){
                    $("h2.table-open-title").html(table_title);
                }
                if($("h2.tab-list-title").length){
                    $("h2.tab-list-title").html(table_title);
                }
            }
            if(table_list){
                if($("#list_school.open_table_list").length){
                    if(schoo_list_first){
                        $("#list_school.open_table_list ul li").remove();
                        $("#list_school.open_table_list ul").prepend(schoo_list_first);
                        $("#list_school.open_table_list ul").append(schoo_list_second);
                        $("#list_school.open_table_list").trigger("afterAjaxUpdate");
                    }else{
                        $("#list_school.open_table_list .school-list").html(table_list);
                        $("#list_school.open_table_list").trigger("afterAjaxUpdate");
                    }
                }
                if($("#nd-school-list").length){
                    $("#nd-school-list").html(table_list);
                    $("#nd-school-list").trigger("afterAjaxUpdate");
                }
                if($("#list_tuition").length){
                    $("#list_tuition").html(table_list);
                    $("#list_tuition").trigger("afterAjaxUpdate");
                }
            }
            TabActive(tab);
            OptionActive(tab);
            if(mapSetting){
                var ajaxMapSetting= $.parseJSON(mapSetting);
                var callBackObj=window[callBack];
                if (typeof callBackObj === "function") callBackObj(ajaxMapSetting);
            }
            ResponsiveTooltips();
            if(typeof gtag == "function"){
                gtag('config', 'UA-61238-4', {'page_path': pushUrl,'page_title': title});
            }
        }
    });
} 
 function TabActive(tab){
    if ($("#secondary_header1 a#"+tab+"_tab").length){
        $("#secondary_header1 a#"+tab+"_tab").siblings("a").show().end().siblings("span").hide().end().next("span").show().end().hide();
    }
}

function OptionActive(tab){
    var baseUrl='/new-mexico/grants-cibola-county-schools-school-district/3501170-school-district';
    if(tab!='all'){
        var tabHref=baseUrl+'/'+tab;
    }else{
        var tabHref=baseUrl;
    }
    if($("select option[data-href='"+tabHref+"']").length){
        $("select option[data-href='"+tabHref+"']").attr("selected",true).siblings("option").attr("selected",false);
        $("select option[data-href='"+tabHref+"']").prop({selected: true});
    }
} 
 function setCircle(){
    $('.mask').each(function(){
        var value = $(this).children('span').text();
        if( value <= 50 ){
            $(this).siblings('.pie_right').css('transform','rotate('+(value*3.6)+'deg)');
        }else{
            $(this).siblings('.pie_right').children('.hidden_right').css('display','block');
            $(this).siblings('.pie_left').css('transform','rotate('+((value-50)*3.6)+'deg)');
        }
    });
} 
 function checkIsMobile(){
    var browser={    
        versions:function(){          
            var u = navigator.userAgent, app = navigator.appVersion;            
            return {                               
                mobile : !!u.match(/AppleWebKit.*Mobile/) || !!u.match(/Windows Phone/) || !!u.match(/Android/) || !!u.match(/MQQBrowser/),
                iPad: u.indexOf('iPad') > -1
            };
        }()
    } 
    if(browser.versions.mobile && !browser.versions.iPad && window.screen.width<767){
        return true;
    }else{
        return false;
    }
} 
 function RowClick(){
    var redirect = true;

    CheckCompareSchools();

    $("li.row_click .compare-btn").unbind('click').click(function() {
        if($(this).html()=="Compare Now"){
            var compare_schools_list = new Array();
            $('.comparing').each(function(){
                compare_schools_list.push($(this).parent().find('.tooltip').attr("schoolidid"));
            });
            var compare_schools_string = compare_schools_list.join("/");
            var href = "/compare-schools/"+compare_schools_string+"#results";
            if(href) {
                window.location = href;
            }
            return false;
        }
        redirect = false;
        CheckCompareSchools();
    });	

    $("li.row_click .checkbox-input").unbind('click').click(function() {
        redirect = false;
        CheckCompareSchools();
        if($("li.row_click .comparing").length>3){
            alert("Please select only up to 3 schools to compare.");
            $(this).parents('.comparing').attr("class","compare_button");
            $(this).find(".compare-btn").html("Add to Compare");
            return false;
        }
    });	

    $("li.row_click .checkboxcss").unbind('click').click(function() {
        redirect = false;
        CheckCompareSchools();
        if($("li.row_click .comparing").length>3){
            alert("Please select only up to 3 schools to compare.");
            $(this).parents('.comparing').attr("class","compare_button");
            $(this).find(".compare-btn").html("Add to Compare");
            return false;
        }
    });

    $("li.row_click").unbind('click').click(function() {
        var href = $(this).find("a").attr("href");
        if(href && redirect) {
            window.location = href;
        }else{
            redirect = true;
        }
    });
}
function CheckCompareSchools(){
    var check_box = $("li.row_click .compare_button");
    check_box.each(function(){
        var checked = $(this).find("input").is(':checked');
        if(checked){
            $(this).attr("class","compare_button comparing");
        }else{
            $(this).attr("class","compare_button");
            $(this).find(".compare-btn").html("Add to Compare");
        }
    });

    if($("li.row_click .comparing").length == 1){
        $("li.row_click .compare_button.comparing .compare-btn").html("Comparing");
    }
    if($("li.row_click .comparing").length == 2 || $("li.row_click .comparing").length == 3){
        $("li.row_click .compare_button.comparing .compare-btn").html("Compare Now");
    }
}

function ShowMoreSchools(){
    var baseUrl='/new-mexico/grants-cibola-county-schools-school-district/3501170-school-district';
    if(typeof location.pathname=='string'){
        var path_name=location.pathname.replace(/\/+/g, '/');
        var decode_pathname=decodeURIComponent(path_name.replace(/\+/g, ' '));
        var decode_base_url=decodeURIComponent(baseUrl.replace(/\+/g, ' '));
        decode_pathname=decode_pathname.toLowerCase();
        decode_base_url=decode_base_url.toLowerCase();
        if(decode_pathname!=decode_base_url){
            var tab = location.pathname.substr(location.pathname.lastIndexOf('/')+1);
        }else{
            var tab='all';
        }
    }else{
        var tab='all';
    }

    var num=$("li#open_show_more_item").data("nextshownum");
    var ajaxRequestUrl=baseUrl+'/'+'tab'+'/'+tab+'/'+'num'+'/'+num;
    $("li#open_show_more_item .throbbershowmoreloading").hide();
    if($("li#open_show_more_item").length){
        $("li#open_show_more_item").click(function(){
            $("li#open_show_more_item #load_image").hide();
            $("li#open_show_more_item .throbbershowmoreloading").show();
            $.ajax({
                url:ajaxRequestUrl,
                type:"GET",
                cache:true,
                success:function(data){
                    var table_list = $(data).find(".ajax_table_list").html();
                    var mapSetting = $(data).find(".ajax_map_setting").html();
                    var callBack = $(data).find(".ajax_map_callback").html();
                    var callBack=$.trim(callBack);
                    if(table_list){
                        if($(".open_table_list").length){
                            $(".open_table_list ul").html(table_list);
                            $(".open_table_list").trigger("afterAjaxUpdate");
                        }
                    }
                    if(mapSetting){
                        if(window["loadAjaxSchoolMarkers"] && map){
                            if(typeof map=="object" && map.hasOwnProperty('_container') && map._container.hasOwnProperty('_leaflet')){
                                map.remove();
                            }
                        }
                        var ajaxMapSetting= $.parseJSON(mapSetting);
                        var callBackObj=window[callBack];
                        if (typeof callBackObj === "function") callBackObj(ajaxMapSetting);
                    }
                }
            });
        });
    }
} 
 function stickyAdsFix(){
    var stickyad=$(".srn_ads_cnt.custom-sticky-ads");
    if(stickyad.length){
        var top=stickyad.offset().top;
        var scrollT = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0;
        var main_height = document.getElementById("main-content").offsetHeight;
        var sec_height = document.getElementById("secondary-content").offsetHeight;
        var footerST=document.documentElement.scrollHeight-document.documentElement.clientHeight-$("#search_footer_div").height();
        var stikyStatrST=document.documentElement.scrollHeight-document.documentElement.clientHeight-$("#search_footer_div").height()-890;
        var stikyEndST=document.documentElement.scrollHeight-document.documentElement.clientHeight-$("#search_footer_div").height()-290;
        var topFinal=document.documentElement.clientHeight-$("#search_footer_div").height()-890;
        if((scrollT+20)<top){
            $(".srn_ads_cnt.custom-sticky-ads > :first-child").css({"position":"static","text-align":"left"});
        }else{
            if(scrollT<stikyStatrST){
                $(".srn_ads_cnt.custom-sticky-ads > :first-child").css({"position":"fixed","top":"20px"});
            }else if((scrollT<=stikyEndST) && (scrollT>=stikyStatrST)){
                var top_val=document.documentElement.clientHeight-620;
                if(top_val>=0){
                    top_val=20;
                }else{
                    top_val=topFinal;
                }
                $(".srn_ads_cnt.custom-sticky-ads > :first-child").css({"position":"fixed","top":top_val+"px"});
            }else if((scrollT<=footerST) && (scrollT>=stikyEndST)){
                var top_val=document.documentElement.clientHeight-620;
                if(top_val>=0){
                    top_val=20;
                }else{
                    top_val=topFinal;
                }
                $(".srn_ads_cnt.custom-sticky-ads > :first-child").css({"position":"fixed","top":top_val+"px"});
            }else{
                top_val=topFinal;
                $(".srn_ads_cnt.custom-sticky-ads > :first-child").css({"position":"fixed","top":top_val+"px"});
            }
        }
    }
} 

