        //Scroll to any anchor tags including scroll to the top
        //$(document).on('click', 'a[href^="#"]', function (event) {
        //    event.preventDefault();

        //    $('html, body').animate({
        //      scrollTop: $($.attr(this, 'href')).offset().top - 140
        //     }, 500);
        //});
        var $root = $('html, body');

        $(function () {
            $('.top-btn').click(function (e) {
                e.preventDefault();
                $root.animate({ scrollTop: '0px' }, 300);
            });
        });

        $('.linkList-Item a[href^="#"]').click(function () {
            $root.animate({
                scrollTop: $('[id="' + $.attr(this, 'href').substr(1) + '"]').offset().top - 210
            }, 1350);

            return false;
        });
        
        $('a[href^="#top"]').click(function () {
            $root.animate({
                scrollTop: $('[id="' + $.attr(this, 'href').substr(1) + '"]').offset().top -210
            }, 1350);

            return false;
        });
        $('a[href^="#latestNews"]').click(function () {
            $root.animate({
                scrollTop: $('[id="' + $.attr(this, 'href').substr(1) + '"]').offset().top -210
            }, 1350);

            return false;
        });

