link.script:
function googleTranslateElementInit() {
    new google.translate.TranslateElement({
        pageLanguage: 'en',
        layout: 0
    }, 'google_translate_element');
}

div.script:
        //Scroll to any anchor tags including scroll to the top
        //Set up variables
        var offsetValue = parseInt($("body").css("padding-top")) + parseInt($("inner-right-col").css("padding-top"));
        var $root = $('html, body');

        $(function () {
            $('.top-btn').click(function (e) {
                e.preventDefault();
                $root.animate({ scrollTop: '0px' }, 300);
            });
        });

        $('.linkList-Item a[href^="#"]').click(function () {
            $root.animate({
                scrollTop: $('[id="' + $.attr(this, 'href').substr(1) + '"]').offset().top - offsetValue
            }, 1350);

            return false;
        });

        $('a[href^="#top"]').click(function () {
            $root.animate({
                scrollTop: $('[name="' + $.attr(this, 'href').substr(1) + '"]').offset().top - offsetValue
            }, 1350);

            return false;
        });

        $(document).ready(function () {

            var words = new Array();

            $('.carousel-caption h3 a').each(function () {
                var words = $(this).text().split(' ');
                var html = '';
                $.each(words, function () {
                    html += '' + this.substring(0, 1) + '' + this.substring(1) + ' ';
                });
                $(this).html(html);
            });

        });

    var _gaq = _gaq || [];
    _gaq.push(['_setAccount', 'UA-80386537-1']);
    _gaq.push(['_trackPageview']);

    (function() {
        var ga = document.createElement('script'); ga.type = 'text/javascript'; ga.async = true;
        ga.src = ('https:' == document.location.protocol ? 'https://ssl' : 'http://www') + '.google-analytics.com/ga.js';
        var s = document.getElementsByTagName('script')[0]; s.parentNode.insertBefore(ga, s);
    })();
