	$(function(){

		$(window).bind("resize", function(){ jsMediaQueries();checkSlideshow();magicLine();upcomingEvents320();stickyCalendarFix(); });


		head.ready("rsMainMenu", function(){
			var additionalHeaders = "", additionalLinks = "";

			// MENU GLOBAL ICONS
			var menuGlobalIcons = [
				["Lunch Menus", "https://www.portnet.org/domain/8"],
				["District Calendar", "https://www.portnet.org/Page/2"],
				["Superintendent&#39s Messages", "https://www.portnet.org/Page/5"]
			];
			var menuIconNum = ("3" == "3;2;1;0") ? 0 : "3";

			for(var i = 0; i < menuIconNum; i++) {
				var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
				additionalLinks += "1, " + menuGlobalIcons[i][0] + ", " + linkCheck + ", _self;";
			}

			if(menuIconNum > 0) {
				additionalHeaders = "Popular Links";
			}

			// RESPONSIVE MAIN MENU
			$("body").csRsMenu({
			        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "right-to-left", // OPTIONS - left-to-right, right-to-left   
			        "primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
                    "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
                    "accentColor"           : "#BABABA", // DEFAULT - #BABABA
			        "menuButtonParent"      : "#sw-mystart-inner",
			        "showMenuBtnText"       : "yes",
			        "menuBtnText"           : "MENU",
			        "showSchools"           : "yes",
			        "showTranslate"         : "yes",
			        "translateId"           : "none",
			        "showAccount"           : "yes",
			        "extraMenuOptions"      : additionalHeaders, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			        "extraMenuItems"        : additionalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			        "siteID"                : "1",
                    "allLoaded"             : function(){ }
			});

		});

		head.ready("rsPagelistMenu", function(){
			// RESPONSIVE PAGELIST MENU
			$("body").csRsPagelistMenu({
			        "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
