	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				tabbedApps();
                $(window).scroll(parallaxScroll);
			break;
			case "768":
				tabbedApps();
                appView768();
			break;
			case "640":
            	tabbedApps();
				appView768();
			break;
			case "480":
            	tabbedApps();
				appView768();
			break;
			case "320":
            	tabbedApps();
				appView();
			break;
		}
	}

    function upcomingEvents320() {
    	switch(csGetBreakPoint()) {
			case "480":
            	$('#hp-region-two .headlines-scroll-ctrl').css("top","50%");
                $('#hp-region-two .headlines-scroll-ctrl').css("height","auto");
			break;
			case "320":
                var headerHeight = $("#hp-region-two div.ui-widget.app.upcomingevents div.ui-widget-header").height() + 30;
                var buttonHeight = $("#hp-region-two div.ui-widget.app.upcomingevents div.ui-widget-detail").height() + 75;
                $('#hp-region-two .headlines-scroll-ctrl').css("top",headerHeight);
                $('#hp-region-two .headlines-scroll-ctrl').css("height",buttonHeight);
			break;
		}
    }
    function magicLine() {

    	if(csGetBreakPoint() != "320"){
    	//SCROLLING LINE ABOVE CHANNEL BAR
			var $el, channelWidth, leftOffset, leftPos, newWidth,
			$mainNav = $("#channel-navigation");
			if(!$("#magic-line").length){
				$mainNav.append("
");
			}
            var $magicLine = $("#magic-line");
			channelWidth = $("li.sw-channel-item.active").width();
			leftOffset = (channelWidth - 76)/2;
			$magicLine
				.width("76px")
				.css("left", $("li.sw-channel-item.active").position().left + leftOffset)
				.data("origLeft", $magicLine.position().left)
				.data("origWidth", $magicLine.width());

			$("li.sw-channel-item").hover(function() {
				$el = $(this);
				channelWidth = $el.width();
				leftOffset = (channelWidth - 76)/2;
				leftPos = $el.position().left + leftOffset;
				newWidth = "76px";
				$magicLine.stop().animate({
					left: leftPos,
					width: newWidth
				});
			}, function() {
				$magicLine.stop().animate({
					left: $magicLine.data("origLeft"),
					width: $magicLine.data("origWidth")
				});    
			});

            //SCROLLING LINE ABOVE THE TABBED APP REGION ONE
            if($("#hp-region-one-tabs").length){
                var $el2, tabWidth, leftOffset2, leftPos2, newWidth2,
                $tabNav = $("#hp-region-one-tabs .tabs-container");

                if($tabNav.height() < 67){
                    if(!$("#tab-line").length){
                        $tabNav.append("
");
                    }
                    var $tabLine = $("#tab-line");
                    tabWidth = $("#hp-region-one-tabs .tab-button").width();
                    leftOffset2 = (tabWidth + 40 - 76)/2;

                    $tabLine
                        .width("76px")
                        .css("left", $("#hp-region-one-tabs .tab-button").position().left + leftOffset2)
                        .data("origLeft", $("#hp-region-one-tabs .tab-button.active").position().left + leftOffset2)
                        .data("origWidth", "76px");

                    $("#hp-region-one-tabs .tab-button").hover(function() {
                        $el2 = $(this);
                        tabWidth = $el2.width();
                        leftOffset2 = (tabWidth + 40 - 76)/2;
                        leftPos2 = $el2.position().left + leftOffset2;
                        newWidth2 = "76px";
                        $tabLine.stop().animate({
                            left: leftPos2,
                            width: newWidth2
                        });
                    }, function() {
                        $tabLine.stop().animate({
                            left: $tabLine.data("origLeft"),
                            width: $tabLine.data("origWidth")
                        });    
                    });

                } 
             }   
         }   
    }

	function mediaRotator() {
		head.ready("mediaRotator", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			        // MULTIMEDIA ROTATOR
			        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
